mapper.update:
UPDATE tbmall_stuff 
SET 
    item_name = #{item_name},
    item_price = #{item_price},
    item_stock = #{item_stock},
    item_description = #{item_description}
WHERE 
    item_id = #{item_id}
    AND admin_no = #{admin_no}

UPDATE tbmall_stuff 
SET item_stock = item_stock - #{quantity}
WHERE item_id = #{item_id}
AND item_stock >= #{quantity}

mapper.select:
SELECT item_stock 
FROM tbmall_stuff 
WHERE item_id = #{item_id}
