import axios from 'axios';
import React, { useState, useEffect } from 'react';
import './App.css';
import { useNavigate } from 'react-router-dom';

// axios Í∏∞Î≥∏ ÏÑ§Ï†ï
axios.defaults.withCredentials = true;

function App() {
  const [shopItems, setShopItems] = useState([]);
  const [cartItems, setCartItems] = useState([]);
  const [isAdmin, setIsAdmin] = useState(false);
  const [deletedItems, setDeletedItems] = useState([]);
  const [newItem, setNewItem] = useState({ itemName: '', price: '', stock: '', description: '' });
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedItem, setSelectedItem] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loginForm, setLoginForm] = useState({ username: '', password: '' });
  const [staffList, setStaffList] = useState([]);
  const [loginStaff, setLoginStaff] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    checkLoginStatus();
    shop();
    checkAdminStatus();
    loadCart();
    loadStaffList();
  }, []);

  // Î°úÍ∑∏Ïù∏ ÏÉÅÌÉú ÌôïÏù∏
  function checkLoginStatus() {
    axios.get('http://localhost:8080/mvc/member/check-login')
      .then(response => {
        setIsLoggedIn(response.data.isLoggedIn);
      })
      .catch(error => {
        console.error('Î°úÍ∑∏Ïù∏ ÏÉÅÌÉú ÌôïÏù∏ Ïã§Ìå®:', error);
        setIsLoggedIn(false);
      });
  }

  // Î°úÍ∑∏Ïù∏ Ï≤òÎ¶¨
  function handleLogin(e) {
    e.preventDefault();
    axios.post('http://localhost:8080/mvc/member/login', loginForm, {
      withCredentials: true
    })
      .then(response => {
        alert('Î°úÍ∑∏Ïù∏ÎêòÏóàÏäµÎãàÎã§.');
        setIsLoggedIn(true);
        checkAdminStatus();
        loadCart();
      })
      .catch(error => {
        alert('Î°úÍ∑∏Ïù∏Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      });
  }

  // Î°úÍ∑∏ÏïÑÏõÉ Ï≤òÎ¶¨
  function handleLogout() {
    axios.post('http://localhost:8080/mvc/member/logout')
      .then(() => {
        setIsLoggedIn(false);
        setIsAdmin(false);
        setCartItems([]);
      })
      .catch(error => {
        console.error('Î°úÍ∑∏ÏïÑÏõÉ Ïã§Ìå®:', error);
      });
  }

  // Í¥ÄÎ¶¨Ïûê ÏÉÅÌÉú ÌôïÏù∏
  function checkAdminStatus() {
    axios.get('http://localhost:8080/mvc/staff/check-admin')
      .then(response => {
        setIsAdmin(response.data.isAdmin);
      })
      .catch(error => {
        console.error('Í¥ÄÎ¶¨Ïûê ÏÉÅÌÉú ÌôïÏù∏ Ïã§Ìå®:', error);
        setIsAdmin(false);
      });
  }

  // ÏÉÅÌíà Î™©Î°ù Ï°∞Ìöå
  function shop() {
    axios.get('http://localhost:8080/mvc/stuff/item/list')
      .then(response => {
        const items = response.data || [];
        setShopItems(Array.isArray(items) ? items : []);
