import axios from 'axios';
import React, { useState, useEffect } from 'react';
import './App.css';
import { useNavigate } from 'react-router-dom';

// axios 기본 설정
axios.defaults.withCredentials = true;

function App() {
  const [shopItems, setShopItems] = useState([]);
  const [cartItems, setCartItems] = useState([]);
  const [isAdmin, setIsAdmin] = useState(false);
  const [deletedItems, setDeletedItems] = useState([]);
  const [newItem, setNewItem] = useState({ itemName: '', price: '', stock: '', description: '' });
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedItem, setSelectedItem] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loginForm, setLoginForm] = useState({ username: '', password: '' });
  const [staffList, setStaffList] = useState([]);
  const [loginStaff, setLoginStaff] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    checkLoginStatus();
    shop();
    checkAdminStatus();
    loadCart();
    loadStaffList();
  }, []);

  // 로그인 상태 확인
  function checkLoginStatus() {
    axios.get('http://localhost:8080/mvc/member/check-login')
      .then(response => {
        setIsLoggedIn(response.data.isLoggedIn);
      })
      .catch(error => {
        console.error('로그인 상태 확인 실패:', error);
        setIsLoggedIn(false);
      });
  }

  // 로그인 처리
  function handleLogin(e) {
    e.preventDefault();
    axios.post('http://localhost:8080/mvc/member/login', loginForm, {
      withCredentials: true
    })
      .then(response => {
        alert('로그인되었습니다.');
        setIsLoggedIn(true);
        checkAdminStatus();
        loadCart();
      })
      .catch(error => {
        alert('로그인에 실패했습니다.');
      });
  }

  // 로그아웃 처리
  function handleLogout() {
    axios.post('http://localhost:8080/mvc/member/logout')
      .then(() => {
        setIsLoggedIn(false);
        setIsAdmin(false);
        setCartItems([]);
      })
      .catch(error => {
        console.error('로그아웃 실패:', error);
      });
  }

  // 관리자 상태 확인
  function checkAdminStatus() {
    axios.get('http://localhost:8080/mvc/staff/check-admin')
      .then(response => {
        setIsAdmin(response.data.isAdmin);
      })
      .catch(error => {
        console.error('관리자 상태 확인 실패:', error);
        setIsAdmin(false);
      });
  }

  // 상품 목록 조회
  function shop() {
    axios.get('http://localhost:8080/mvc/stuff/item/list')
      .then(response => {
        const items = response.data || [];
        setShopItems(Array.isArray(items) ? items : []);
      })
      .catch(error => {
        console.error('상품 목록 조회 실패:', error);
        setShopItems([]);
      });
  }

  // 상품 등록
  function registerItem(e) {
    e.preventDefault();
    axios.post('http://localhost:8080/mvc/stuff/item/register', newItem)
      .then(response => {
        alert('상품이 등록되었습니다.');
        shop();
        setNewItem({ itemName: '', price: '', stock: '', description: '' });
      })
      .catch(error => {
        console.error('상품 등록 실패:', error);
        alert('상품 등록에 실패했습니다.');
      });
  }

  // 장바구니 조회
  function loadCart() {
    axios.get('http://localhost:8080/mvc/stuff/cart')
      .then(response => {
        const items = response.data || [];
        setCartItems(Array.isArray(items) ? items : []);
      })
      .catch(error => {
        console.error('장바구니 조회 실패:', error);
        setCartItems([]);
      });
  }

  // 장바구니에 추가
  function addItem(itemId, quantity) {
    const params = new URLSearchParams();
    params.append('itemId', itemId);
    params.append('quantity', quantity);

    axios.post('http://localhost:8080/mvc/stuff/cart/add', params, {
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded'
      }
    })
    .then(response => {
      alert('상품이 장바구니에 추가되었습니다.');
      loadCart();
    })
    .catch(error => {
      console.error('장바구니 추가 실패:', error);
      alert(error.response?.data?.message || '장바구니 추가에 실패했습니다.');
    });
  }

  // 장바구니에서 제거
  function removeFromCart(cartId) {
    axios.post(`http://localhost:8080/mvc/stuff/cart/remove?cartId=${cartId}`)
      .then(() => {
        alert('상품이 장바구니에서 제거되었습니다.');
        loadCart();
      })
      .catch(error => {
        alert('상품 제거에 실패했습니다.');
      });
  }

  // 결제 처리
  function checkout() {
    axios.post('http://localhost:8080/mvc/stuff/cart/checkout')
      .then(() => {
        alert('결제가 완료되었습니다.');
        loadCart();
      })
      .catch(error => {
        alert('결제에 실패했습니다.');
      });
  }

  // 상품 삭제
  function deleteItem(itemId) {
    if (window.confirm('정말 이 상품을 삭제하시겠습니까?')) {
      axios.post(`http://localhost:8080/mvc/stuff/item/delete?itemId=${itemId}`)
        .then(() => {
          alert('상품이 삭제되었습니다.');
          shop();
        })
        .catch(error => {
          alert('상품 삭제에 실패했습니다.');
        });
    }
  }

  // 삭제된 상품 목록 조회
  function loadDeletedItems() {
    axios.get('http://localhost:8080/mvc/stuff/item/deleted')
      .then(response => {
        setDeletedItems(response.data || []);
      })
      .catch(error => {
        console.error('삭제된 상품 목록 조회 실패:', error);
      });
  }

  // 상품 복구
  function restoreItem(itemId) {
    axios.post(`http://localhost:8080/mvc/stuff/item/restore?itemId=${itemId}`)
      .then(() => {
        alert('상품이 복구되었습니다.');
        loadDeletedItems();
        shop();
      })
      .catch(error => {
        alert('상품 복구에 실패했습니다.');
      });
  }

  // 상품 검색 기능
  const filteredItems = shopItems.filter(item =>
    item.itemName.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // 상품 수정
  function updateItem(e) {
    e.preventDefault();
    axios.post('http://localhost:8080/mvc/stuff/item/update', selectedItem)
      .then(response => {
        alert('상품이 수정되었습니다.');
        setEditMode(false);
        setSelectedItem(null);
        shop();
      })
      .catch(error => {
        alert('상품 수정에 실패했습니다.');
      });
  }

  // 직원 목록 조회 함수 추가
  function loadStaffList() {
    axios.get('http://localhost:8080/mvc/staff/list')
      .then(response => {
        setStaffList(response.data || []);
      })
      .catch(error => {
        console.error('직원 목록 조회 실패:', error);
      });
  }

  // 직원 삭제 함수 추가
  function confirmDelete(bno) {
    if(window.confirm('이 직원을 삭제하시겠습니까?')) {
      axios.post(`http://localhost:8080/mvc/staff/remove?bno=${bno}`)
        .then(() => {
          alert('직원이 삭제되었습니다.');
          loadStaffList();
        })
        .catch(error => {
          alert('직원 삭제에 실패했습니다.');
        });
    }
  }

  // 직원 수정 페이지로 이동
  function editStaff(bno) {
    navigate(`/staff/edit?bno=${bno}`);
  }

  // 삭제된 직원 목록 페이지로 이동
  function goToRemovedStaffList() {
    navigate('/staff/removelist');
  }

  // JSP의 top-menu 부분을 React 컴포넌트로 변환
  const TopMenu = () => (
    <div className="top-menu">
      <h1 className="section-title" style={{ margin: 0 }}>물건 목록</h1>
      <div>
        {location.search.includes('message=addedToCart') && (
          <span style={{ color: 'green', marginRight: '20px' }}>
            장바구니에 추가되었습니다!
          </span>
        )}
        <a href="/mvc/stuff/cart" className="cart-link">
          🛒 장바구니
        </a>
      </div>
    </div>
  );

  // JSP의 admin-menu 부분을 React 컴포넌트로 변환
  const AdminMenu = () => (
    isAdmin && (
      <div className="admin-menu">
        <button onClick={() => navigate('/stuff/item/register')}>물건 등록</button>
        <button onClick={() => navigate('/stuff/item/deleted')}>삭제된 물건 목록</button>
        <button onClick={goToRemovedStaffList}>삭제된 직원 목록</button>
      </div>
    )
  );

  // 직원 목록 테이블 컴포넌트
  const StaffTable = () => (
    isAdmin && (
      <>
        <h2 className="section-title">직원 목록</h2>
        <table className="staff-table">
          <thead>
            <tr>
              <th>직원번호</th>
              <th>아이디</th>
              <th>이름</th>
              <th>관리자 여부</th>
              <th>관리</th>
            </tr>
          </thead>
          <tbody>
            {staffList.map(staff => (
              <tr key={staff.bno}>
                <td>{staff.bno}</td>
                <td>{staff.id}</td>
                <td>{staff.btext}</td>
                <td>{staff.admins === 1 ? '관리자' : '일반 직원'}</td>
                <td>
                  <button onClick={() => confirmDelete(staff.bno)}>삭제</button>
                  <button onClick={() => editStaff(staff.bno)}>수정</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </>
    )
  );

  return (
    <div className="App">
      <TopMenu />
      <AdminMenu />

      {/* 비밀번호 변경 버튼 */}
      {loginStaff && (
        <button 
          onClick={() => navigate(`/staff/edit?bno=${loginStaff.bno}`)} 
          className="edit-link"
        >
          비밀번호 변경
        </button>
      )}

      {/* 기존 로그인/로그아웃 버튼 */}
      {!isLoggedIn ? (
        <button onClick={() => navigate('/staff/login')}>로그인</button>
      ) : (
        <button onClick={handleLogout}>로그아웃</button>
      )}

      {/* 기존 상품 목록 및 장바구니 관련 컴포넌트들 */}
      {/* ... */}

      {/* 직원 목록 테이블 */}
      <StaffTable />
    </div>
  );
}

export default App;
