import axios from 'axios';
import React, { useState, useEffect } from 'react';
import './App.css';
import { Routes, Route, useNavigate, useLocation } from 'react-router-dom';
import StaffEdit from './components/StaffEdit';
import ItemList from './components/ItemList';
import ItemRegister from './components/ItemRegister';
import StaffRegister from './components/Register';
import DeletedItems from './components/DeletedItems';
import Cart from './components/Cart';
import RemovedStaff from './components/RemovedStaff';

// axios Í∏∞Î≥∏ ÏÑ§Ï†ï
axios.defaults.withCredentials = true;

// StaffTableÏùÑ Î≥ÑÎèÑÏùò Ïª¥Ìè¨ÎÑåÌä∏Î°ú Î∂ÑÎ¶¨
const StaffTable = ({ staffList, onDelete, onEdit }) => {
  useEffect(() => {
    console.log('StaffTable Î¶¨Î†åÎçîÎßÅ:', staffList);
  }, [staffList]);

  return (
    <>
      <h2 className="section-title">ÏßÅÏõê Î™©Î°ù</h2>
      <table className="staff-table">
        <thead>
          <tr>
            <th>ÏßÅÏõêÎ≤àÌò∏</th>
            <th>ÏïÑÏù¥Îîî</th>
            <th>Ïù¥Î¶Ñ</th>
            <th>Í¥ÄÎ¶¨Ïûê Ïó¨Î∂Ä</th>
            <th>Í¥ÄÎ¶¨</th>
          </tr>
        </thead>
        <tbody>
          {staffList.map(staff => (
            <tr key={staff.member_no + '-' + staff.member_id}>
              <td>{staff.member_no}</td>
              <td>{staff.member_id}</td>
              <td>{staff.member_nick}</td>
              <td>{staff.admins === 1 ? 'Í¥ÄÎ¶¨Ïûê' : 'ÏùºÎ∞ò ÏßÅÏõê'}</td>
              <td>
                <button onClick={() => onDelete(staff.member_no)}>ÏÇ≠Ï†ú</button>
                <button onClick={() => onEdit(staff.member_no)}>ÏàòÏ†ï</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
};

// App Ïª¥Ìè¨ÎÑåÌä∏
function App() {
  // ÏÉÅÌÉú Î≥ÄÏàòÎì§
  const [isAdmin, setIsAdmin] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loginForm, setLoginForm] = useState({ username: '', password: '' });
  const [staffList, setStaffList] = useState([]);
  // const [adminList , setAdminList] = useState([]);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    checkLoginStatus();
    loadStaffList();
    // loadAdminList();
  }, []);

  // Î°úÍ∑∏Ïù∏ ÏÉÅÌÉú ÌôïÏù∏
  function checkLoginStatus() {
    axios.get('http://localhost:8080/mvc/staff/check-login')
      .then(response => {
        setIsLoggedIn(response.data.isLoggedIn);
        setIsAdmin(response.data.isAdmin);
      })
      .catch(error => {
        console.error('Î°úÍ∑∏Ïù∏ ÏÉÅÌÉú ÌôïÏù∏ Ïã§Ìå®:', error);
        setIsLoggedIn(false);
        setIsAdmin(false);
      });
  }

  // Î°úÍ∑∏Ïù∏ Ï≤òÎ¶¨
  function handleLogin(e) {
    e.preventDefault();
    const params = new URLSearchParams();
    params.append('staffId', loginForm.username);
    params.append('password', loginForm.password);

    axios.post('http://localhost:8080/mvc/staff/login', params, {
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded'
      }
    })
      .then(response => {
        if (response.data.success) {
          setIsLoggedIn(true);
          setIsAdmin(response.data.isAdmin);
          navigate('/stuff/item/list'); // ÏàòÏ†ïÎêú Í≤ΩÎ°ú
        } else {
          alert(response.data.message || 'Î°úÍ∑∏Ïù∏Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.'); // Î©îÏãúÏßÄ Í∞úÏÑ†
        }
      })
      .catch(error => {
        console.error('Î°úÍ∑∏Ïù∏ ÏöîÏ≤≠ Ïã§Ìå®:', error); // ÏΩòÏÜîÏóê ÏóêÎü¨ Î°úÍ∑∏ Ï∂îÍ∞Ä
        alert('Î°úÍ∑∏Ïù∏Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§. ÏÑúÎ≤ÑÏóê Î¨∏Ï†úÍ∞Ä ÏûàÏäµÎãàÎã§.'); // ÏóêÎü¨ Î©îÏãúÏßÄ Í∞úÏÑ†
      });
  }

  // Î°úÍ∑∏ÏïÑÏõÉ Ï≤òÎ¶¨
  function handleLogout() {
    axios.post('http://localhost:8080/mvc/staff/logout')
      .then(response => {
        if (response.data.success) {
          setIsLoggedIn(false);
          setIsAdmin(false);
          navigate('/'); // Î°úÍ∑∏ÏïÑÏõÉ ÌõÑ Î©îÏù∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
        }
      })
      .catch(error => {
        console.error('Î°úÍ∑∏ÏïÑÏõÉ Ïã§Ìå®:', error);
      });
  }

  // ÏßÅÏõê Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
  const loadStaffList = async () => {
    try {
      console.log('ÏßÅÏõê Î™©Î°ù ÏÉàÎ°úÍ≥†Ïπ® ÏãúÏûë');
      
      // Ï∫êÏãúÎêú Îç∞Ïù¥ÌÑ∞Î•º ÏÇ¨Ïö©ÌïòÏßÄ ÏïäÎèÑÎ°ù ÌÉÄÏûÑÏä§ÌÉ¨ÌîÑ Ï∂îÍ∞Ä
      const response = await axios.get(`http://localhost:8080/mvc/staff/list?_t=${Date.now()}`);
      console.log('ÏÉàÎ°ú Î∞õÏùÄ ÏßÅÏõê Î™©Î°ù:', response.data);
      
      // ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏ Ï†ÑÏóê Îç∞Ïù¥ÌÑ∞ Ï†ïÎ†¨
      const sortedStaffList = [...response.data].sort((a, b) => a.member_no - b.member_no);
      console.log('Ï†ïÎ†¨Îêú ÏßÅÏõê Î™©Î°ù:', sortedStaffList);
      
      setStaffList(sortedStaffList);
      console.log('ÏßÅÏõê Î™©Î°ù ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏ ÏôÑÎ£å');
    } catch (error) {
      console.error('ÏßÅÏõê Î™©Î°ù Ï°∞Ìöå Ïã§Ìå®:', error);
    }
  };

  // //Í¥ÄÎ¶¨Ïûê Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
  // function loadAdminList(){
  //     axios.get('http://localhost:8080/mvc/staff/adminlist')
  //       .then(response => {
  //         setAdminList(response.data | []);
  //       })
  //       .catch(error =>{
  //         console.error('Í¥ÄÎ¶¨Ïûê Î™©Î°ù Ï°∞Ìöå Ïã§Ìå®:',error);
  //       })
  // }


  // ÏßÅÏõê ÏÇ≠Ï†ú
  function confirmDelete(member_no) {
    if (window.confirm('Ïù¥ ÏßÅÏõêÏùÑ ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?')) {
      const params = new URLSearchParams();
      params.append('member_no', member_no);

      axios.post('http://localhost:8080/mvc/staff/remove', params, {
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded'
        },
        withCredentials: true
      })
        .then(response => {
          if (response.data === 'redirect:/staff/list' || response.status === 200) {
            alert('ÏßÅÏõêÏù¥ ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.');
            loadStaffList();
          } else {
            throw new Error('ÏÇ≠Ï†úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
          }
        })
        .catch(error => {
          console.error('ÏßÅÏõê ÏÇ≠Ï†ú Ïã§Ìå®:', error);
          alert('ÏßÅÏõê ÏÇ≠Ï†úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
        });
    }
  }

  // ÏßÅÏõê ÏàòÏ†ï
  function editStaff(member_no) {
    navigate(`/staff/edit?member_no=${member_no}`);
  }

  // TopMenu Ïª¥Ìè¨ÎÑåÌä∏
  const TopMenu = () => (
    <div>
      <div className="top-menu">
        <h1 className="section-title" style={{ margin: 0 }}>Î¨ºÍ±¥ Î™©Î°ù</h1>
        <div>
          {isLoggedIn && (
            <a href="/stuff/cart" className="cart-link">
              üõí Ïû•Î∞îÍµ¨Îãà
            </a>
          )}
        </div>
      </div>
    </div>
  );

  // AdminMenu Ïª¥Ìè¨ÎÑåÌä∏
  const AdminMenu = () => (
    isAdmin && (
      <div className="admin-menu">
        <button onClick={() => navigate('/stuff/item/register')}>Î¨ºÍ±¥ Îì±Î°ù</button>
        <button onClick={() => navigate('/staff/register')}>ÏÇ¨Ïõê Îì±Î°ù</button>
        <button onClick={() => navigate('/stuff/item/deleted')}>ÏÇ≠Ï†úÎêú Í±¥ Î™©Î°ù</button>
        <button onClick={() => navigate('/staff/removelist')}>ÏÇ≠Ï†úÎêú ÏßÅÏõê Î™©Î°ù</button>
      </div>
    )
  );

  return (
    <div className="App">
      <TopMenu />
      <AdminMenu />
      
      {/* Î°úÍ∑∏Ïù∏/Î°úÍ∑∏ÏïÑÏõÉ Î≤ÑÌäº */}
      {!isLoggedIn ? (
        <form onSubmit={handleLogin}>
          <input
            type="text"
            placeholder="ÏïÑÏù¥Îîî"
            value={loginForm.username}
            onChange={(e) => setLoginForm({ ...loginForm, username: e.target.value })}
            required
          />
          <input
            type="password"
            placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏"
            value={loginForm.password}
            onChange={(e) => setLoginForm({ ...loginForm, password: e.target.value })}
            required
          />
          <button type="submit">Î°úÍ∑∏Ïù∏</button>
        </form>
      ) : (
        <button onClick={handleLogout}>Î°úÍ∑∏ÏïÑÏõÉ</button>
      )}

      {/* ÎùºÏö∞Ìä∏ ÏÑ§Ï†ï ÏàòÏ†ï */}
      <Routes>
        {/* Î©îÏù∏ ÌéòÏù¥ÏßÄÏóê ItemList ÌëúÏãú */}
        <Route path="/" element={<ItemList isLoggedIn={isLoggedIn} isAdmin={isAdmin} />} />
        <Route path="/staff/edit" element={<StaffEdit onUpdate={loadStaffList} />} />
        <Route path="/stuff/item/list" element={<ItemList isLoggedIn={isLoggedIn} isAdmin={isAdmin} />} />
        <Route path="/staff/register" element={<StaffRegister />} />
        <Route path="/stuff/item/register" element={<ItemRegister />} />
        <Route path="/stuff/item/deleted" element={<DeletedItems />} />
        <Route path="/stuff/cart" element={<Cart />} />
        <Route path="/staff/removelist" element={<RemovedStaff />} />
      </Routes>

      {/* Í¥ÄÎ¶¨ÏûêÏùº Í≤ΩÏö∞ÏóêÎßå StaffTable ÌëúÏãúÌïòÍ≥† Î™©Î°ùÎèÑ ÏÉàÎ°úÍ≥†Ïπ® */}
      {isAdmin && (
        <StaffTable 
          staffList={staffList}
          onDelete={confirmDelete}
          onEdit={editStaff}
          key={JSON.stringify(staffList)}
        />
      )}
    </div>
  );
}

export default App;
