import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Cart() {
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    fetchCartItems();
  }, []);

  const fetchCartItems = async () => {
    try {
      const response = await axios.get('http://localhost:8080/mvc/stuff/cart/items', {
        withCredentials: true
      });
      setCartItems(response.data);
    } catch (error) {
      console.error('장바구니 목록 조회 실패:', error);
    }
  };

  const removeItem = async (cartId) => {
    try {
      await axios.delete(`http://localhost:8080/mvc/stuff/cart/remove/${cartId}`, {
        withCredentials: true
      });
      fetchCartItems();
    } catch (error) {
      console.error('장바구니 아이템 삭제 실패:', error);
    }
  };

  const updateQuantity = async (cartId, newQuantity) => {
    try {
      await axios.put(`http://localhost:8080/mvc/stuff/cart/update/${cartId}`, {
        quantity: newQuantity
      }, {
        withCredentials: true
      });
      fetchCartItems();
    } catch (error) {
      console.error('수량 업데이트 실패:', error);
    }
  };

  const addToCart = async (itemId, quantity) => {
    if (!itemId || quantity <= 0) {
      alert('유효한 아이템 ID와 수량을 입력하세요.');
      return;
    }
    
    try {
      const response = await axios.get(`http://localhost:8080/mvc/stuff/cart/add?itemId=`+itemId +'&quantity='+quantity);
        
     
    } catch (error) {
      if (error.response && error.response.status === 401) {
        alert('로그인이 필요합니다.');
      } else {
        console.error('장바구니 추가 실패:', error);
      }
    }
  };

  const calculateTotal = () => {
    return cartItems.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  const handleCheckout = async () => {
    try {
      await axios.post('http://localhost:8080/mvc/stuff/cart/checkout', {}, {
        withCredentials: true
      });
      alert('주문이 완료되었습니다.');
      fetchCartItems();
    } catch (error) {
      console.error('주문 처리 실패:', error);
      alert('주문 처리 중 오류가 발생했습니다.');
    }
  };

  return (
    <div className="cart-container">
      <h2>장바구니</h2>
      {cartItems.length === 0 ? (
        <p>장바구니가 비어있습니다.</p>
      ) : (
        <>
          <div className="cart-items">
            {cartItems.map(item => (
              <div key={item.cartId} className="cart-item">
                <h3>{item.itemName}</h3>
                <p>가격: {item.price.toLocaleString()}원</p>
                <input 
                  type="number" 
                  min="1" 
                  value={item.quantity}
                  onChange={(e) => updateQuantity(item.cartId, parseInt(e.target.value))}
                />
                <button onClick={() => removeItem(item.cartId)}>삭제</button>
                <button onClick={() => addToCart(item.itemId, 1)}>장바구니에 추가</button>
              </div>
            ))}
          </div>
          <div className="cart-total">
            <h3>총 금액: {calculateTotal().toLocaleString()}원</h3>
            <button onClick={handleCheckout}>주문하기</button>
          </div>
        </>
      )}
    </div>
  );
}

export default Cart;
