import axios from 'axios';
import React, { useState, useEffect } from 'react';
import './App.css';
import { useNavigate, useLocation } from 'react-router-dom';

// axios 기본 설정
axios.defaults.withCredentials = true;

function App() {
  const [shopItems, setShopItems] = useState([]);
  const [cartItems, setCartItems] = useState([]);
  const [isAdmin, setIsAdmin] = useState(false);
  const [deletedItems, setDeletedItems] = useState([]);
  const [newItem, setNewItem] = useState({ itemName: '', price: '', stock: '', description: '' });
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedItem, setSelectedItem] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loginForm, setLoginForm] = useState({ username: '', password: '' });
  const [staffList, setStaffList] = useState([]);
  const [loginStaff, setLoginStaff] = useState(null);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    checkLoginStatus();
    shop();
    loadCart();
    loadStaffList();
  }, []);

  // 로그인 상태 확인
  function checkLoginStatus() {
    axios.get('http://localhost:8080/mvc/staff/check-login')
      .then(response => {
        setIsLoggedIn(response.data.isLoggedIn);
        setIsAdmin(response.data.isAdmin);
        if (response.data.isLoggedIn) {
          // 추가적으로 로그인된 직원 정보를 설정할 수 있습니다.
          // 예: setLoginStaff(response.data.staff);
        }
      })
      .catch(error => {
        console.error('로그인 상태 확인 실패:', error);
        setIsLoggedIn(false);
        setIsAdmin(false);
      });
  }

  // 로그인 처리
  function handleLogin(e) {
    e.preventDefault();
    const params = new URLSearchParams();
    params.append('staffId', loginForm.username);
    params.append('password', loginForm.password);

    axios.post('http://localhost:8080/mvc/staff/login', params, {
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded'
      }
    })
      .then(response => {
        if (response.data.success) {
          setIsLoggedIn(true);
          setIsAdmin(response.data.isAdmin);
          navigate('/stuff/item/list');
        } else {
          alert(response.data.message);
        }
      })
      .catch(error => {
        alert('로그인에 실패했습니다.');
      });
  }

  // 로그아웃 처리
  function handleLogout() {
    axios.post('http://localhost:8080/mvc/staff/logout')
      .then(response => {
        if (response.data.success) {
          setIsLoggedIn(false);
          setIsAdmin(false);
          setCartItems([]);
          setLoginStaff(null);
        }
      })
      .catch(error => {
        console.error('로그아웃 실패:', error);
      });
  }

  // 상품 목록 조회
  function shop() {
    axios.get('http://localhost:8080/mvc/stuff/item/list')
      .then(response => {
        const items = response.data || [];
        setShopItems(items);
      })
      .catch(error => {
        console.error('상품 목록 조회 실패:', error);
      });
  }

  // 장바구니 불러오기
  function loadCart() {
    axios.get('http://localhost:8080/mvc/stuff/cart')
      .then(response => {
        setCartItems(response.data || []);
      })
      .catch(error => {
        console.error('장바구니 불러오기 실패:', error);
      });
  }

  // 직원 목록 불러오기
  function loadStaffList() {
    axios.get('http://localhost:8080/mvc/staff/list')
      .then(response => {
        setStaffList(response.data || []);
      })
      .catch(error => {
        console.error('직원 목록 조회 실패:', error);
      });
  }

  // 직원 삭제
  function confirmDelete(bno) {
    if(window.confirm('이 직원을 삭제하시겠습니까?')) {
      const params = new URLSearchParams();
      params.append('bno', bno);

      axios.post('http://localhost:8080/mvc/staff/remove', params, {
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded'
        }
      })
        .then(response => {
          if (response.data.success) {
            alert('직원이 삭제되었습니다.');
            loadStaffList();
          } else {
            alert(response.data.message);
          }
        })
        .catch(error => {
          alert('직원 삭제에 실패했습니다.');
        });
    }
  }

  // 직원 수정
  function editStaff(bno) {
    navigate(`/staff/edit?bno=${bno}`);
  }

  // 비밀번호 변경
  function changePassword(bno, currentPassword, newPassword) {
    const staffDto = {
      bno: bno,
      password: newPassword
    };

    axios.post('http://localhost:8080/mvc/staff/changePassword',
      staffDto,
      {
        params: { currentPassword },
        headers: {
          'Content-Type': 'application/json'
        }
      }
    )
      .then(response => {
        if (response.data.success) {
          alert('비밀번호가 변경되었습니다.');
          navigate('/stuff/item/list');
        } else {
          alert(response.data.message);
        }
      })
      .catch(error => {
        alert('비밀번호 변경에 실패했습니다.');
      });
  }

  // 삭제된 직원 목록 조회
  function loadDeletedStaffList() {
    axios.get('http://localhost:8080/mvc/staff/removelist')
      .then(response => {
        if (response.data.success) {
          setDeletedItems(response.data.list);
        } else {
          alert(response.data.message);
        }
      })
      .catch(error => {
        console.error('삭제된 직원 목록 조회 실패:', error);
      });
  }

  // 직원 복구
  function restoreStaff(bno) {
    const params = new URLSearchParams();
    params.append('bno', bno);

    axios.post('http://localhost:8080/mvc/staff/restore', params, {
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded'
      }
    })
      .then(response => {
        if (response.data.success) {
          alert('직원이 복구되었습니다.');
          loadDeletedStaffList();
          loadStaffList();
        } else {
          alert(response.data.message);
        }
      })
      .catch(error => {
        alert('직원 복구에 실패했습니다.');
      });
  }

  // JSP의 top-menu 부분을 React 컴포넌트로 변환
  const TopMenu = () => (
    <div className="top-menu">
      <h1 className="section-title" style={{ margin: 0 }}>물건 목록</h1>
      <div>
        {location.search.includes('message=addedToCart') && (
          <span style={{ color: 'green', marginRight: '20px' }}>
            장바구니에 추가되었습니다!
          </span>
        )}
        <a href="/mvc/stuff/cart" className="cart-link">
          🛒 장바구니
        </a>
      </div>
    </div>
  );

  // JSP의 admin-menu 부분을 React 컴포넌트로 변환
  const AdminMenu = () => (
    isAdmin && (
      <div className="admin-menu">
        <button onClick={() => navigate('/stuff/item/register')}>물건 등록</button>
        <button onClick={() => navigate('/stuff/item/deleted')}>삭제된 물건 목록</button>
        <button onClick={() => navigate('/staff/removelist')}>삭제된 직원 목록</button>
      </div>
    )
  );

  // 직원 목록 테이블 컴포넌트
  const StaffTable = () => (
    isAdmin && (
      <>
        <h2 className="section-title">직원 목록</h2>
        <table className="staff-table">
          <thead>
            <tr>
              <th>직원번호</th>
              <th>아이디</th>
              <th>이름</th>
              <th>관리자 여부</th>
              <th>관리</th>
            </tr>
          </thead>
          <tbody>
            {staffList.map(staff => (
              <tr key={staff.bno}>
                <td>{staff.bno}</td>
                <td>{staff.id}</td>
                <td>{staff.btext}</td>
                <td>{staff.admins === 1 ? '관리자' : '일반 직원'}</td>
                <td>
                  <button onClick={() => confirmDelete(staff.bno)}>삭제</button>
                  <button onClick={() => editStaff(staff.bno)}>수정</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </>
    )
  );

  return (
    <div className="App">
      <TopMenu />
      <AdminMenu />

      {/* 비밀번호 변경 버튼 */}
      {isLoggedIn && (
        <button 
          onClick={() => navigate(`/staff/edit?bno=${loginStaff ? loginStaff.bno : ''}`)} 
          className="edit-link"
        >
          비밀번호 변경
        </button>
      )}

      {/* 로그인/로그아웃 버튼 */}
      {!isLoggedIn ? (
        <button onClick={() => navigate('/staff/login')}>로그인</button>
      ) : (
        <button onClick={handleLogout}>로그아웃</button>
      )}

      {/* 기존 상품 목록 및 장바구니 관련 컴포넌트들 */}
      {/* ... */}

      {/* 직원 목록 테이블 */}
      <StaffTable />
    </div>
  );
}

export default App;
