import axios from 'axios';
import React, { useState, useEffect } from 'react';
import './App.css';
import { useNavigate, useLocation } from 'react-router-dom';

// axios Í∏∞Î≥∏ ÏÑ§Ï†ï
axios.defaults.withCredentials = true;

function App() {
  const [shopItems, setShopItems] = useState([]);
  const [cartItems, setCartItems] = useState([]);
  const [isAdmin, setIsAdmin] = useState(false);
  const [deletedItems, setDeletedItems] = useState([]);
  const [newItem, setNewItem] = useState({ itemName: '', price: '', stock: '', description: '' });
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedItem, setSelectedItem] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loginForm, setLoginForm] = useState({ username: '', password: '' });
  const [staffList, setStaffList] = useState([]);
  const [loginStaff, setLoginStaff] = useState(null);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    checkLoginStatus();
    shop();
    loadCart();
    loadStaffList();
  }, []);

  // Î°úÍ∑∏Ïù∏ ÏÉÅÌÉú ÌôïÏù∏
  function checkLoginStatus() {
    axios.get('http://localhost:8080/mvc/staff/check-login')
      .then(response => {
        setIsLoggedIn(response.data.isLoggedIn);
        setIsAdmin(response.data.isAdmin);
        if (response.data.isLoggedIn) {
          // Ï∂îÍ∞ÄÏ†ÅÏúºÎ°ú Î°úÍ∑∏Ïù∏Îêú ÏßÅÏõê Ï†ïÎ≥¥Î•º ÏÑ§Ï†ïÌï† Ïàò ÏûàÏäµÎãàÎã§.
          // Ïòà: setLoginStaff(response.data.staff);
        }
      })
      .catch(error => {
        console.error('Î°úÍ∑∏Ïù∏ ÏÉÅÌÉú ÌôïÏù∏ Ïã§Ìå®:', error);
        setIsLoggedIn(false);
        setIsAdmin(false);
      });
  }

  // Î°úÍ∑∏Ïù∏ Ï≤òÎ¶¨
  function handleLogin(e) {
    e.preventDefault();
    const params = new URLSearchParams();
    params.append('staffId', loginForm.username);
    params.append('password', loginForm.password);

    axios.post('http://localhost:8080/mvc/staff/login', params, {
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded'
      }
    })
      .then(response => {
        if (response.data.success) {
          setIsLoggedIn(true);
          setIsAdmin(response.data.isAdmin);
          navigate('/stuff/item/list');
        } else {
          alert(response.data.message);
        }
      })
      .catch(error => {
        alert('Î°úÍ∑∏Ïù∏Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      });
  }

  // Î°úÍ∑∏ÏïÑÏõÉ Ï≤òÎ¶¨
  function handleLogout() {
    axios.post('http://localhost:8080/mvc/staff/logout')
      .then(response => {
        if (response.data.success) {
          setIsLoggedIn(false);
          setIsAdmin(false);
          setCartItems([]);
          setLoginStaff(null);
        }
      })
      .catch(error => {
        console.error('Î°úÍ∑∏ÏïÑÏõÉ Ïã§Ìå®:', error);
      });
  }

  // ÏÉÅÌíà Î™©Î°ù Ï°∞Ìöå
  function shop() {
    axios.get('http://localhost:8080/mvc/stuff/item/list')
      .then(response => {
        const items = response.data || [];
        setShopItems(items);
      })
      .catch(error => {
        console.error('ÏÉÅÌíà Î™©Î°ù Ï°∞Ìöå Ïã§Ìå®:', error);
      });
  }

  // Ïû•Î∞îÍµ¨Îãà Î∂àÎü¨Ïò§Í∏∞
  function loadCart() {
    axios.get('http://localhost:8080/mvc/stuff/cart')
      .then(response => {
        setCartItems(response.data || []);
      })
      .catch(error => {
        console.error('Ïû•Î∞îÍµ¨Îãà Î∂àÎü¨Ïò§Í∏∞ Ïã§Ìå®:', error);
      });
  }

  // ÏßÅÏõê Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
  function loadStaffList() {
    axios.get('http://localhost:8080/mvc/staff/list')
      .then(response => {
        setStaffList(response.data || []);
      })
      .catch(error => {
        console.error('ÏßÅÏõê Î™©Î°ù Ï°∞Ìöå Ïã§Ìå®:', error);
      });
  }

  // ÏßÅÏõê ÏÇ≠Ï†ú
  function confirmDelete(bno) {
    if(window.confirm('Ïù¥ ÏßÅÏõêÏùÑ ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?')) {
      const params = new URLSearchParams();
      params.append('bno', bno);

      axios.post('http://localhost:8080/mvc/staff/remove', params, {
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded'
        }
      })
        .then(response => {
          if (response.data.success) {
            alert('ÏßÅÏõêÏù¥ ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.');
            loadStaffList();
          } else {
            alert(response.data.message);
          }
        })
        .catch(error => {
          alert('ÏßÅÏõê ÏÇ≠Ï†úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
        });
    }
  }

  // ÏßÅÏõê ÏàòÏ†ï
  function editStaff(bno) {
    navigate(`/staff/edit?bno=${bno}`);
  }

  // ÎπÑÎ∞ÄÎ≤àÌò∏ Î≥ÄÍ≤Ω
  function changePassword(bno, currentPassword, newPassword) {
    const staffDto = {
      bno: bno,
      password: newPassword
    };

    axios.post('http://localhost:8080/mvc/staff/changePassword',
      staffDto,
      {
        params: { currentPassword },
        headers: {
          'Content-Type': 'application/json'
        }
      }
    )
      .then(response => {
        if (response.data.success) {
          alert('ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä Î≥ÄÍ≤ΩÎêòÏóàÏäµÎãàÎã§.');
          navigate('/stuff/item/list');
        } else {
