import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useSearchParams } from 'react-router-dom';

function StaffEdit() {
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();
  const member_no = searchParams.get('member_no');
  const [isAdmin, setIsAdmin] = useState(false);

  const [staffData, setStaffData] = useState({
    member_id: '',
    member_nick: '',
    member_gender: '',
    member_birth: '',
    member_phone: '',
    member_email: '',
    admins: 0
  });

  const [currentPassword, setCurrentPassword] = useState('');

  useEffect(() => {
    const fetchStaffData = async () => {
      try {
        const response = await axios.get('http://localhost:8080/mvc/staff/list', {
          withCredentials: true
        });
        
        if (response.data) {
          const staff = response.data.find(staff => staff.member_no === parseInt(member_no));
          
          if (staff) {
            const formattedData = {
              ...staff,
              member_birth: staff.member_birth ? 
                new Date(staff.member_birth).toISOString().split('T')[0] : ''
            };
            setStaffData(formattedData);
          } else {
            throw new Error('직원을 찾을 수 없습니다.');
          }
        }
      } catch (error) {
        console.error('직원 정보를 가져오는데 실패했습니다:', error);
        alert('직원 정보를 불러오는데 실패했습니다.');
        navigate('/');
      }
    };

    if (member_no) {
      fetchStaffData();
    }
  }, [member_no, navigate]);

  useEffect(() => {
    const checkAdminStatus = async () => {
      try {
        const response = await axios.get('http://localhost:8080/mvc/staff/check-login');
        setIsAdmin(response.data.admins === 1);
      } catch (error) {
        console.error('관리자 상태 확인 실패:', error);
        navigate('/');
      }
    };
    
    checkAdminStatus();
  }, [navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const params = new URLSearchParams();
      
      const requiredFields = {
        member_no: member_no,
        member_id: staffData.member_id,
        member_nick: staffData.member_nick,
        member_gender: staffData.member_gender,
        member_birth: staffData.member_birth ? 
          new Date(staffData.member_birth).toISOString().split('T')[0] : '',
        member_phone: staffData.member_phone,
        member_email: staffData.member_email,
        currentPassword: staffData.admins === 1 ? '' : currentPassword
      };

      Object.entries(requiredFields).forEach(([key, value]) => {
        params.append(key, value);
      });

      console.log('전송할 데이터:', Object.fromEntries(params));

      const response = await axios.post(
        'http://localhost:8080/mvc/staff/edit',
        params,
        {
          headers: {
            'Content-Type': 'application/x-www-form-urlencoded'
          },
          withCredentials: true
        }
      );

      if (response.data === 'redirect:/staff/list' || response.status === 200) {
        alert('직원 정보가 수정되었습니다.');
        navigate('/');
      } else {
        throw new Error('수정에 실패했습니다.');
      }
    } catch (error) {
      console.error('직원 정보 수정 실패:', error);
      if (error.response) {
        console.error('서버 응답:', error.response.data);
      }
      alert('직원 정보 수정에 실패했습니다.');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setStaffData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  return (
    <div className="edit-form-container">
      <h2>직원 정보 수정</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>아이디</label>
          <input
            type="text"
            name="member_id"
            value={staffData.member_id}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>이름</label>
          <input
            type="text"
            name="member_nick"
            value={staffData.member_nick}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>성별</label>
          <select
            name="member_gender"
            value={staffData.member_gender}
            onChange={handleChange}
            required
          >
            <option value="M">남성</option>
            <option value="F">여성</option>
          </select>
        </div>
        <div className="form-group">
          <label>생년월일</label>
          <input
            type="date"
            name="member_birth"
            value={staffData.member_birth}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>전화번호</label>
          <input
            type="tel"
            name="member_phone"
            value={staffData.member_phone}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>이메일</label>
          <input
            type="email"
            name="member_email"
            value={staffData.member_email}
            onChange={handleChange}
            required
          />
        </div>
        {staffData.admins !== 1 && (
          <div className="form-group">
            <label>현재 비밀번호</label>
            <input
              type="password"
              value={currentPassword}
              onChange={(e) => setCurrentPassword(e.target.value)}
              required
            />
          </div>
        )}
        <div className="button-group">
          <button type="submit">수정</button>
          <button type="button" onClick={() => navigate('/')}>
            취소
          </button>
        </div>
      </form>
    </div>
  );
}

export default StaffEdit;
