import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import './ItemDetail.css';
import { SERVER_URL } from '../config';

function ItemDetail({ isLoggedIn, isAdmin }) {
  const { itemId } = useParams();
  const navigate = useNavigate();
  const [item, setItem] = useState(null);
  const [boards, setBoards] = useState([]);
  const [newBoard, setNewBoard] = useState({
    board_title: '',
    board_content: '',
    rating: 5 // 기본값 5점으로 설정
  });
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [pageSize, setPageSize] = useState(10);
  const [totalPages, setTotalPages] = useState(0);

  // 상품 정보와 게시글 목록 로드
  useEffect(() => {
    const loadItemDetail = async () => {
      try {
        const response = await axios.get(`${SERVER_URL}/mvc/stuff/item/detail/${itemId}`, {
          withCredentials: true
        });
        
        if (response.data.status === 'success') {
          setItem(response.data.item);
        } else {
          throw new Error('상품 정보를 불러오는데 실패했습니다.');
        }
      } catch (error) {
        console.error('상품 정보 로딩 실패:', error);
        alert('상품 정보를 불러오는데 실패했습니다.');
        setItem(null);
      } finally {
        setLoading(false);
      }
    };

    // 게시글 목록 로드
    const loadBoards = async () => {
      try {
        const response = await axios.get('/board/list', {
          params: { currentPage, pageSize },
          withCredentials: true,
        });
        
        console.log('서버 응답:', response.data); // 디버깅용 로그
        
        // 데이터가 있는지 확인하고 안전하게 처리
        if (response.data && Array.isArray(response.data.boards)) {
          const filteredBoards = response.data.boards.filter(board => 
            board && board.board_delete === 0
          );
          setBoards(filteredBoards);
          setTotalPages(response.data.totalPages || 1);
        } else {
          console.error('잘못된 데이터 형식:', response.data);
          setBoards([]);
          setTotalPages(1);
        }
      } catch (error) {
        console.error('게시판 목록을 불러오는 중 오류 발생:', error);
        setBoards([]);
        setTotalPages(1);
      }
    };

    loadItemDetail();
    loadBoards();
  }, [itemId, currentPage, pageSize]);

  // 게시글 작성 처리
  const handleBoardSubmit = async (e) => {
    e.preventDefault();
    if (!isLoggedIn) {
      alert('게시글을 작성하려면 로그인이 필요합니다.');
      return;
    }

    try {
      const params = new URLSearchParams();
      params.append('member_no', localStorage.getItem('member_no'));
      params.append('board_title', newBoard.board_title);
      params.append('board_content', newBoard.board_content);
      params.append('rating', newBoard.rating);

      const response = await axios.post('/board/write', params, {
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded'
        }
      });

      if (response.data.success) {
        alert('게시글이 등록되었습니다.');
        setBoards([response.data.board, ...boards]);
        setNewBoard({ board_title: '', board_content: '', rating: 5 });
      }
    } catch (error) {
      console.error('게시글 등록 실패:', error);
      alert('게시글 등록에 실패했습니다.');
    }
  };

  const handleDelete = async (board_no) => {
    try {
      if (!isAdmin) {
        alert('관리자 권한이 필요합니다.');
        return;
      }

      if (window.confirm('이 글을 삭제하시겠습니까?')) {
        const response = await axios.post('/board/delete', 
          { board_no: board_no },
          {
            withCredentials: true,
            headers: {
              'Content-Type': 'application/json',
            },
          }
        );

        if (response.data.success) {
          alert('해당 글이 삭제되었습니다.');
          const updatedBoards = boards.filter(board => board.board_no !== board_no);
          setBoards(updatedBoards);
        }
      }
    } catch (error) {
      console.error('글 삭제 실패:', error);
      alert('글 삭제 중 오류가 발생했습니다.');
    }
  };

  const readContent = (board_no) => {
    navigate(`/board/read?board_no=${board_no}`);
  };

  const handlePageChange = (page) => {
    if (page >= 1 && page <= totalPages) {
      setCurrentPage(page);
    }
  };

  if (loading) {
    return <div>로딩 중...</div>;
  }

  if (!item) {
    return <div>상품을 찾을 수 없습니다.</div>;
  }

  return (
    <div className="item-detail-container">
      <div className="item-detail">
        <div className="item-image">
          {item?.image_url ? (
            <img 
              src={item.image_url}
              alt={item.item_name}
              onError={(e) => {
                e.target.src = 'https://via.placeholder.com/400x200';
              }}
            />
          ) : (
            <img 
              src='https://via.placeholder.com/400x200'
              alt="기본 이미지"
            />
          )}
        </div>
        <div className="item-info">
          <h2>{item?.item_name}</h2>
          <p className="price">가격: {(item?.item_price || 0).toLocaleString()}원</p>
          <p className="stock">재고: {(item?.item_stock || 0).toLocaleString()}개</p>
          <p className="description">{item?.item_description}</p>
        </div>
      </div>

      <div className="board-section">
        <h3>상품 게시판</h3>
        
        {isLoggedIn && (
          <form onSubmit={handleBoardSubmit} className="board-form">
            <input
              type="text"
              value={newBoard.board_title}
              onChange={(e) => setNewBoard({...newBoard, board_title: e.target.value})}
              placeholder="제목을 입력하세요"
              required
            />
            <textarea
              value={newBoard.board_content}
              onChange={(e) => setNewBoard({...newBoard, board_content: e.target.value})}
              placeholder="내용을 입력하세요"
              required
            />
            <div className="rating-input">
              <label>별점:</label>
              <select 
                value={newBoard.rating}
                onChange={(e) => setNewBoard({...newBoard, rating: parseInt(e.target.value)})}
              >
                <option value="1">★</option>
                <option value="2">★★</option>
                <option value="3">★★★</option>
                <option value="4">★★★★</option>
                <option value="5">★★★★★</option>
              </select>
            </div>
            <button type="submit">게시글 작성</button>
          </form>
        )}

        <div className="board-list">
          {!boards || boards.length === 0 ? (
            <p>등록된 게시글이 없습니다.</p>
          ) : (
            <table className="board-table">
              <thead>
                <tr>
                  <th>번호</th>
                  <th>제목</th>
                  <th>내용</th>
                  <th>별점</th>
                  <th>작성일</th>
                  {isAdmin && <th>관리</th>}
                </tr>
              </thead>
              <tbody>
                {boards.map((board, index) => board && (
                  <tr key={board.board_no}>
                    <td>{(currentPage - 1) * pageSize + index + 1}</td>
                    <td>{board.board_title || '제목 없음'}</td>
                    <td>
                      <span
                        className="board-content-link"
                        onClick={() => readContent(board.board_no)}
                      >
                        {board.board_content || '내용 없음'}
                      </span>
                    </td>
                    <td>{'★'.repeat(board.rating || 0)}</td>
                    <td>{board.board_writedate || '-'}</td>
                    {isAdmin && (
                      <td>
                        <button
                          onClick={() => handleDelete(board.board_no)}
                          className="delete-button"
                        >
                          삭제
                        </button>
                      </td>
                    )}
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>

        <div className="pagination">
          <button onClick={() => handlePageChange(currentPage - 1)} disabled={currentPage === 1}>
            이전
          </button>
          {Array.from({ length: totalPages }, (_, i) => (
            <button
              key={i + 1}
              onClick={() => handlePageChange(i + 1)}
              className={currentPage === i + 1 ? 'active' : ''}
            >
              {i + 1}
            </button>
          ))}
          <button onClick={() => handlePageChange(currentPage + 1)} disabled={currentPage === totalPages}>
            다음
          </button>
        </div>
      </div>
    </div>
  );
}

export default ItemDetail;
