import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ItemList.css';

function ItemList({ onAddToCart, onDeleteItem, onEditItem, isLoggedIn, isAdmin }) {
  const [items, setItems] = useState([]);
  const [quantities, setQuantities] = useState({});
  const [adminNo, setAdminNo] = useState(null);

  useEffect(() => {
    loadItems();
    checkAdminStatus();
  }, []);

  const checkAdminStatus = async () => {
    try {
      const response = await axios.get('http://localhost:8080/mvc/staff/check-login');
      setAdminNo(response.data.adminNo);
    } catch (error) {
      console.error('Í¥ÄÎ¶¨Ïûê ÏÉÅÌÉú ÌôïÏù∏ Ïã§Ìå®:', error);
    }
  };

  const loadItems = async () => {
    try {
      const response = await axios.get('http://localhost:8080/mvc/stuff/item/list');
      setItems(response.data);
      // Í∞Å ÏïÑÏù¥ÌÖúÏùò ÏàòÎüâÏùÑ 1Î°ú Ï¥àÍ∏∞Ìôî
      const initialQuantities = {};
      response.data.forEach(item => {
        initialQuantities[item.item_id] = 1;
      });
      setQuantities(initialQuantities);
    } catch (error) {
      console.error('Î¨ºÍ±¥ Î™©Î°ù Î°úÎî© Ïã§Ìå®:', error);
    }
  };

  const handleQuantityChange = (itemId, value) => {
    setQuantities(prev => ({
      ...prev,
      [itemId]: Math.max(1, Math.min(value, items.find(item => item.item_id === itemId).item_stock))
    }));
  };

  return (
    <div className="item-list">
      <h2>Î¨ºÍ±¥ Î™©Î°ù</h2>
      <div className="items-container">
        {items.map(item => (
          <div key={item.item_id} className="item-card">
            <h3>{item.item_name}</h3>
            <p>Í∞ÄÍ≤©: {item.item_price}Ïõê</p>
            <p>Ïû¨Í≥†: {item.item_stock}Í∞ú</p>
            <p>{item.item_description}</p>
            
            {/* Í¥ÄÎ¶¨Ïûê Í∏∞Îä• - admin_noÍ∞Ä 1Ïù∏ Í≤ΩÏö∞ÏóêÎßå ÌëúÏãú */}
            {adminNo === 1 && (
              <div className="admin-controls">
                <button 
                  className="edit-button"
                  onClick={() => onEditItem(item.item_id)}
                >
                  ‚úèÔ∏è ÏàòÏ†ï
                </button>
                <button 
                  className="delete-button"
                  onClick={() => {
                    if (window.confirm('Ï†ïÎßê Ïù¥ Î¨ºÍ±¥ÏùÑ ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?')) {
                      onDeleteItem(item.item_id);
                    }
                  }}
                >
                  üóëÔ∏è ÏÇ≠Ï†ú
                </button>
              </div>
            )}
            
            {/* Ïû•Î∞îÍµ¨Îãà Ï∂îÍ∞Ä Í∏∞Îä• */}
            {isLoggedIn && item.item_stock > 0 && (
              <div className="cart-controls">
                <input
                  type="number"
                  min="1"
                  max={item.item_stock}
                  value={quantities[item.item_id]}
                  onChange={(e) => handleQuantityChange(item.item_id, parseInt(e.target.value))}
                />
                <button 
                  className="add-to-cart-button"
                  onClick={() => onAddToCart(item.item_id, quantities[item.item_id])}
                  disabled={item.item_stock === 0}
                >
                  üõí Ïû•Î∞îÍµ¨ÎãàÏóê Ï∂îÍ∞Ä
                </button>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default ItemList;
