import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import './ItemDetail.css';
import { SERVER_URL } from '../config';

function ItemDetail() {
  const { itemId } = useParams();
  const navigate = useNavigate();
  const [item, setItem] = useState(null);
  const [boards, setBoards] = useState([]);
  const [newBoard, setNewBoard] = useState({
    board_title: '',
    board_content: ''
  });
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const [currentComment, setCurrentComment] = useState(1);
  const [cpageSize, setCpageSize] = useState(5);
  const [totalComment, setTotalComment] = useState(0);
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [pageSize, setPageSize] = useState(10);
  const [totalPages, setTotalPages] = useState(0);
  const [userInfo, setUserInfo] = useState(null);
  const [rating, setRating] = useState(5);
  const [hover, setHover] = useState(0);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);

  // 댓글 목록 로드 함수 정의
  const loadComments = async (boardNo) => {
    try {
      console.log('댓글 목록 요청 - board_no:', boardNo);
      
      const response = await axios.get(`${SERVER_URL}/mvc/board/commentlist`, {
        params: { 
          board_no: boardNo,
          currentComment: currentComment, 
          cpageSize: cpageSize 
        },
        withCredentials: true
      });

      console.log('댓글 목록 응답:', response.data);

      if (response.data && Array.isArray(response.data.comments)) {
        setComments(response.data.comments);
        setTotalComment(response.data.totalComment || 0);
      } else {
        console.warn('댓글 데이터가 없거나 형식이 잘못되었습니다:', response.data);
        setComments([]);
        setTotalComment(0);
      }
    } catch (error) {
      console.error('댓글 목록을 불러오는 중 오류 발생:', error);
      setComments([]);
      setTotalComment(0);
    }
  };

  // checkLoginStatus 함수 수정
  const checkLoginStatus = async () => {
    try {
      const response = await axios.post(`${SERVER_URL}/mvc/staff/check-login`, {}, {
        withCredentials: true,
        headers: {
          'Content-Type': 'application/json'
        }
      });

      console.log('로그인 상태 확인 응답:', response.data);
      
      if (response.data && response.data.isLoggedIn) {
        setIsLoggedIn(true);
        setIsAdmin(response.data.isAdmin);
        localStorage.setItem('member_no', response.data.admin_no);
        localStorage.setItem('member_role', response.data.isAdmin ? 'ROLE_ADMIN' : 'ROLE_USER');
        setUserInfo(response.data);  // 전체 사용자 정보 저장
      } else {
        setIsLoggedIn(false);
        setIsAdmin(false);
        localStorage.removeItem('member_no');
        localStorage.removeItem('member_role');
        setUserInfo(null);
      }
    } catch (error) {
      console.error('로그인 상태 확인 실패:', error);
      setIsLoggedIn(false);
      setIsAdmin(false);
      setUserInfo(null);
    }
  };

  // 상품 정보와 댓글 목록 로드
  useEffect(() => {
    const loadItemDetail = async () => {
      try {
        const response = await axios.get(`${SERVER_URL}/mvc/stuff/item/detail/${itemId}`, {
          withCredentials: true
        });
        
        if (response.data.status === 'success') {
          setItem(response.data.item);
        } else {
          throw new Error('상품 정보를 불러오는데 실패했습니다.');
        }
      } catch (error) {
        console.error('상품 정보 로딩 실패:', error);
        alert('상품 정보를 불러오는데 실패했습니다.');
        setItem(null);
      } finally {
        setLoading(false);
      }
    };

    // 로그인 상태 체크 및 사용자 정보 로드
    const storedUserInfo = localStorage.getItem('userInfo');
    if (storedUserInfo) {
      try {
        const parsedUserInfo = JSON.parse(storedUserInfo);
        setUserInfo(parsedUserInfo);
        console.log('저장된 사용자 정보:', parsedUserInfo);
      } catch (error) {
        console.error('사용자 정보 파싱 오류:', error);
      }
    }

    const loadData = async () => {
      try {
        await checkLoginStatus(); // 비동기 함수로 변경
        await loadItemDetail();
        if (itemId) {
          await loadComments(itemId);
        }
      } catch (error) {
        console.error('데이터 로드 중 오류 발생:', error);
      }
    };
    
    loadData();

    // 로그인 상태 변경 감지를 위한 이벤트 리스너
    window.addEventListener('storage', checkLoginStatus);
    
    return () => {
      window.removeEventListener('storage', checkLoginStatus);
    };
  }, [itemId, currentComment]);

  // 댓글 작성 함수 수정
  const handleCommentSubmit = async (e) => {
    e.preventDefault();

    if (!newComment.trim()) {
      alert('댓글 내용을 입력해 주세요.');
      return;
    }

    try {
      if (!itemId) {
        throw new Error('상품 정보가 없습니다.');
      }

      // 1. 먼저 게시글 정보를 가져와서 세션에 저장
      const boardResponse = await axios.get(`${SERVER_URL}/mvc/board/read`, {
        params: { 
          board_no: itemId 
        },
        withCredentials: true
      });

      if (!boardResponse.data) {
        throw new Error('게시글 정보를 가져올 수 없습니다.');
      }

      // 2. 댓글 작성 POST 요청
      const commentData = {
        board_no: itemId,
        member_no: localStorage.getItem('member_no'),
        comment_content: newComment,
        rating: rating
      };

      console.log('댓글 작성 요청 데이터:', commentData);

      const response = await axios.post(
        `${SERVER_URL}/mvc/board/comment`,
        JSON.stringify(commentData), // JSON 문자열로 변환
        {
          withCredentials: true,
          headers: {
            'Content-Type': 'application/json'
          }
        }
      );

      console.log('댓글 작성 응답:', response.data);

      if (response.data.success) {
        setNewComment('');
        setRating(5);
        await loadComments(itemId);
        alert('댓글이 등록되었습니다.');
      } else {
        throw new Error(response.data.message || '댓글 등록에 실패했습니다.');
      }
    } catch (error) {
      console.error('댓글 작성 중 오류 발생:', error);
      if (error.response?.status === 401 || 
          error.response?.data?.message?.includes('세션')) {
        alert('로그인이 필요한 서비스입니다.');
        navigate('/staff/login');
      } else {
        alert(error.response?.data?.message || '댓글 작성에 실패했습니다.');
      }
    }
  };

  const handleDelete = async (comment_no) => {
    try {
      if (!isAdmin) {
        alert('관리자 권한이 필요합니다.');
        return;
      }

      if (window.confirm('이 댓글을 삭제하시겠습니까?')) {
        const response = await axios.post(
          `${SERVER_URL}/mvc/board/comment/delete`,
          { comment_no },
          {
            withCredentials: true,
            headers: {
              'Content-Type': 'application/json'
            }
          }
        );

        if (response.data.success) {
          alert('댓글이 삭제되었습니다.');
          await loadComments(itemId);
        } else {
          throw new Error('댓글 삭제에 실패했습니다.');
        }
      }
    } catch (error) {
      console.error('댓글 삭제 실패:', error);
      alert(error.response?.data?.message || '댓글 삭제 중 오류가 발생했습니다.');
    }
  };

  const handleCommentPageChange = (page) => {
    if (page >= 1 && page <= totalComment) {
      setCurrentComment(page);
    }
  };

  // 댓글 목록 표시 부분 추가
  const renderComments = () => {
    if (!comments || comments.length === 0) {
      return <p className="no-comments">등록된 댓글이 없습니다.</p>;
    }

    return (
      <div className="comments-container">
        {comments.map((comment, index) => (
          <div key={comment.comment_no || index} className="comment-item">
            <div className="comment-header">
              <span className="comment-author">{comment.member_nick || '익명'}</span>
              <div className="rating-display">
                {[1, 2, 3, 4, 5].map((star) => (
                  <span
                    key={star}
                    className={`star ${star <= (comment.rating || 0) ? 'filled' : 'empty'}`}
                  >
                    ★
                  </span>
                ))}
              </div>
              <span className="comment-date">
                {new Date(comment.comment_writedate).toLocaleDateString()}
              </span>
            </div>
            <p className="comment-content">{comment.comment_content}</p>
            {isAdmin && (
              <button
                onClick={() => handleDelete(comment.comment_no)}
                className="delete-btn"
              >
                삭제
              </button>
            )}
          </div>
        ))}
        
        {/* 페이지네이션 */}
        <div className="pagination">
          <button
            onClick={() => handleCommentPageChange(currentComment - 1)}
            disabled={currentComment <= 1}
          >
            이전
          </button>
          <span>{currentComment} / {totalComment}</span>
          <button
            onClick={() => handleCommentPageChange(currentComment + 1)}
            disabled={currentComment >= totalComment}
          >
            다음
          </button>
        </div>
      </div>
    );
  };

  // CommentForm 컴포넌트 수정
  const CommentForm = () => {
    const handleCommentChange = (e) => {
      setNewComment(e.target.value); // 직접 이벤트의 value를 사용
    };

    const handleSubmit = (e) => {
      e.preventDefault();
      handleCommentSubmit(e);
    };

    return (
      <form onSubmit={handleSubmit} className="comment-form">
        <div className="rating-input">
          <span>별점:</span>
          <div className="star-rating">
            {[1, 2, 3, 4, 5].map((star) => (
              <label key={star}>
                <input
                  type="radio"
                  name="rating"
                  value={star}
                  checked={rating === star}
                  onChange={() => setRating(star)}
                />
                <span 
                  className={`star ${star <= (hover || rating) ? 'on' : ''}`}
                  onMouseEnter={() => setHover(star)}
                  onMouseLeave={() => setHover(0)}
                >
                  ★
                </span>
              </label>
            ))}
          </div>
        </div>
        <textarea
          value={newComment}
          onChange={handleCommentChange}
          placeholder="댓글을 입력하세요..."
          required
          rows={4}
          className="comment-textarea"
          style={{ 
            width: '100%',
            minHeight: '100px',
            padding: '10px',
            margin: '10px 0',
            resize: 'vertical'
          }}
        />
        <button type="submit">댓글 작성</button>
      </form>
    );
  };

  if (loading) {
    return <div>로딩 중...</div>;
  }

  if (!item) {
    return <div>상품을 찾을 수 없습니다.</div>;
  }

  return (
    <div className="item-detail-container">
      <div className="item-detail">
        <div className="item-image">
          {item?.image_url ? (
            <img 
              src={item.image_url}
              alt={item.item_name}
              onError={(e) => {
                e.target.src = 'https://via.placeholder.com/400x200';
              }}
            />
          ) : (
            <img 
              src='https://via.placeholder.com/400x200'
              alt="기본 이미지"
            />
          )}
        </div>
        <div className="item-info">
          <h2>{item?.item_name}</h2>
          <p className="price">가격: {(item?.item_price || 0).toLocaleString()}원</p>
          <p className="stock">재고: {(item?.item_stock || 0).toLocaleString()}개</p>
          <p className="description">{item?.item_description}</p>
        </div>
      </div>

      <div className="comments-section">
        <h3>상품 후기</h3>
        {isLoggedIn ? <CommentForm /> : (
          <p className="login-required">
            댓글을 작성하려면 <button onClick={() => navigate('/staff/login')}>로그인</button>이 필요합니다.
          </p>
        )}
        {renderComments()}
      </div>
    </div>
  );
}

export default ItemDetail;
