  const [isLoggedIn, setIsLoggedIn] = useState(false);  // Î°úÍ∑∏Ïù∏ Ïó¨Î∂Ä
  const [loginForm, setLoginForm] = useState({ username: '', password: '' });  // Î°úÍ∑∏Ïù∏ Ìèº Îç∞Ïù¥ÌÑ∞
  const [staffList, setStaffList] = useState([]);  // ÏßÅÏõê Î™©Î°ù
  const navigate = useNavigate();  // ÌéòÏù¥ÏßÄ Ïù¥ÎèôÏùÑ ÏúÑÌïú navigate ÌõÖ
  const location = useLocation();  // ÌòÑÏû¨ URL Ï†ïÎ≥¥

  useEffect(() => {
    checkLoginStatus();  // Î°úÍ∑∏Ïù∏ ÏÉÅÌÉú ÌôïÏù∏
    loadStaffList();  // ÏßÅÏõê Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
  }, []);

  // Î°úÍ∑∏Ïù∏ ÏÉÅÌÉú ÌôïÏù∏
  function checkLoginStatus() {
    axios.get('http://localhost:8080/mvc/staff/check-login')
      .then(response => {
        setIsLoggedIn(response.data.isLoggedIn);  // Î°úÍ∑∏Ïù∏ ÏÉÅÌÉú Í∞±Ïã†
        setIsAdmin(response.data.isAdmin);  // Í¥ÄÎ¶¨Ïûê ÏÉÅÌÉú Í∞±Ïã†
      })
      .catch(error => {
        console.error('Î°úÍ∑∏Ïù∏ ÏÉÅÌÉú ÌôïÏù∏ Ïã§Ìå®:', error);
        setIsLoggedIn(false);  // Ïã§Ìå®Ïãú Î°úÍ∑∏Ïù∏ ÏÉÅÌÉúÎ•º falseÎ°ú
        setIsAdmin(false);  // Í¥ÄÎ¶¨Ïûê ÏÉÅÌÉúÎ•º falseÎ°ú
      });
  }

  // Î°úÍ∑∏Ïù∏ Ï≤òÎ¶¨
  function handleLogin(e) {
    e.preventDefault();
    const params = new URLSearchParams();
    params.append('staffId', loginForm.username);
    params.append('password', loginForm.password);

    axios.post('http://localhost:8080/mvc/staff/login', params, {
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded'
      }
    })
      .then(response => {
        if (response.data.success) {
          setIsLoggedIn(true);  // Î°úÍ∑∏Ïù∏ ÏÑ±Í≥µÏãú Î°úÍ∑∏Ïù∏ ÏÉÅÌÉú true
          setIsAdmin(response.data.isAdmin);  // Í¥ÄÎ¶¨Ïûê Ïó¨Î∂Ä Í∞±Ïã†
          navigate('/stuff/item/list');  // Î¨ºÍ±¥ Î™©Î°ù ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
        } else {
          alert(response.data.message || 'Î°úÍ∑∏Ïù∏Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
        }
      })
      .catch(error => {
        console.error('Î°úÍ∑∏Ïù∏ ÏöîÏ≤≠ Ïã§Ìå®:', error);
        alert('Î°úÍ∑∏Ïù∏Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§. ÏÑúÎ≤ÑÏóê Î¨∏Ï†úÍ∞Ä ÏûàÏäµÎãàÎã§.');
      });
  }

  // Î°úÍ∑∏ÏïÑÏõÉ Ï≤òÎ¶¨
  function handleLogout() {
    axios.post('http://localhost:8080/mvc/staff/logout')
      .then(response => {
        if (response.data.success) {
          setIsLoggedIn(false);  // Î°úÍ∑∏ÏïÑÏõÉ ÌõÑ Î°úÍ∑∏Ïù∏ ÏÉÅÌÉú false
          setIsAdmin(false);  // Í¥ÄÎ¶¨Ïûê ÏÉÅÌÉú false
          navigate('/');  // Î©îÏù∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
        }
      })
      .catch(error => {
        console.error('Î°úÍ∑∏ÏïÑÏõÉ Ïã§Ìå®:', error);
      });
  }

  // ÏßÅÏõê Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
  function loadStaffList() {
    axios.get('http://localhost:8080/mvc/staff/list')
      .then(response => {
        setStaffList(response.data || []);  // ÏßÅÏõê Î™©Î°ù ÏóÖÎç∞Ïù¥Ìä∏
      })
      .catch(error => {
        console.error('ÏßÅÏõê Î™©Î°ù Ï°∞Ìöå Ïã§Ìå®:', error);
      });
  }

  // ÏßÅÏõê ÏÇ≠Ï†ú ÌôïÏù∏
  function confirmDelete(bno) {
    if (window.confirm('Ïù¥ ÏßÅÏõêÏùÑ ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?')) {
      const params = new URLSearchParams();
      params.append('bno', bno);

      axios.post('http://localhost:8080/mvc/staff/remove', params, {
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded'
        }
      })
        .then(response => {
          if (response.data.success) {
            alert('ÏßÅÏõêÏù¥ ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.');
            loadStaffList();  // ÏßÅÏõê Î™©Î°ù Ïû¨Î∂àÎü¨Ïò§Í∏∞
          } else {
            alert(response.data.message);
          }
        })
        .catch(error => {
          alert('ÏßÅÏõê ÏÇ≠Ï†úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
        });
    }
  }

  // ÏßÅÏõê ÏàòÏ†ï
  function editStaff(bno) {
    navigate(`/staff/edit?bno=${bno}`);  // ÏàòÏ†ï ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
  }

  // Ïû•Î∞îÍµ¨ÎãàÏóê Î¨ºÍ±¥ Ï∂îÍ∞ÄÌïòÎäî Ìï®Ïàò
  const handleAddToCart = async (itemId, quantity = 1) => {
    try {
      if (!isLoggedIn) {
        alert('Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.');
        return;
      }

      // Ïû•Î∞îÍµ¨Îãà Ï∂îÍ∞Ä ÏöîÏ≤≠
      const params = new URLSearchParams();
      params.append('itemId', itemId);
      params.append('quantity', quantity);

      const response = await axios.post('http://localhost:8080/mvc/stuff/cart/add', params, {
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded'
        }
      });

      if (response.data.status === 'success') {
        alert('Ïû•Î∞îÍµ¨ÎãàÏóê Ï∂îÍ∞ÄÎêòÏóàÏäµÎãàÎã§.');
        // Ïû•Î∞îÍµ¨Îãà ÌéòÏù¥ÏßÄÎ°ú Ïù¥ÎèôÌïòÍ±∞ÎÇò Îã§Î•∏ ÌïÑÏöîÌïú ÏûëÏóÖ ÏàòÌñâ
      } else {
        alert(response.data.message || 'Ïû•Î∞îÍµ¨Îãà Ï∂îÍ∞ÄÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      }
    } catch (error) {
      console.error('Ïû•Î∞îÍµ¨Îãà Ï∂îÍ∞Ä Ï§ë Ïò§Î•ò:', error);
      if (error.response && error.response.status === 400) {
        alert('Ïû¨Í≥†Í∞Ä Î∂ÄÏ°±Ìï©ÎãàÎã§.');
      } else {
        alert('Ïû•Î∞îÍµ¨Îãà Ï∂îÍ∞Ä Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
      }
    }
  };

  // TopMenu Ïª¥Ìè¨ÎÑåÌä∏ (Î¨ºÍ±¥ Î™©Î°ù ÏÉÅÎã® Î©îÎâ¥)
  const TopMenu = () => (
    <div>
      <div className="top-menu">
        <h1 className="section-title" style={{ margin: 0 }}>Î¨ºÍ±¥ Î™©Î°ù</h1>
        <div>
          {location.search.includes('message=addedToCart') && (
            <span style={{ color: 'green', marginRight: '20px' }}>
              Ïû•Î∞îÍµ¨ÎãàÏóê Ï∂îÍ∞ÄÎêòÏóàÏäµÎãàÎã§!
            </span>
          )}
          <a href="/stuff/cart" className="cart-link">
            üõí Ïû•Î∞îÍµ¨Îãà
          </a>
        </div>
      </div>
    </div>
  );

  // AdminMenu Ïª¥Ìè¨ÎÑåÌä∏ (Í¥ÄÎ¶¨Ïûê Î©îÎâ¥)
  const AdminMenu = () => (
    isAdmin && (
      <div className="admin-menu">
        <button onClick={() => navigate('/stuff/item/register')}>Î¨ºÍ±¥ Îì±Î°ù</button>
        <button onClick={() => navigate('/stuff/item/deleted')}>ÏÇ≠Ï†úÎêú Í±¥ Î™©Î°ù</button>
        <button onClick={() => navigate('/staff/removelist')}>ÏÇ≠Ï†úÎêú ÏßÅÏõê Î™©Î°ù</button>
      </div>
    )
  );

  // StaffTable Ïª¥Ìè¨ÎÑåÌä∏ (ÏßÅÏõê Î™©Î°ù ÌÖåÏù¥Î∏î)
  const StaffTable = () => (
    isAdmin && (
      <>
        <h2 className="section-title">ÏßÅÏõê Î™©Î°ù</h2>
        <table className="staff-table">
          <thead>
            <tr>
              <th>ÏßÅÏõêÎ≤àÌò∏</th>
              <th>ÏïÑÏù¥Îîî</th>
              <th>Ïù¥Î¶Ñ</th>
              <th>Í¥ÄÎ¶¨Ïûê Ïó¨Î∂Ä</th>
              <th>Í¥ÄÎ¶¨</th>
            </tr>
          </thead>
          <tbody>
            {staffList.map(staff => (
              <tr key={staff.member_no}>
                <td>{staff.member_no}</td>
                <td>{staff.member_id}</td>
                <td>{staff.member_nick}</td>
                <td>{staff.admins === 1 ? 'Í¥ÄÎ¶¨Ïûê' : 'ÏùºÎ∞ò ÏßÅÏõê'}</td>
                <td>
                  <button onClick={() => confirmDelete(staff.member_no)}>ÏÇ≠Ï†ú</button>
                  <button onClick={() => editStaff(staff.member_no)}>ÏàòÏ†ï</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </>
    )
  );

  return (
    <div className="App">
      <TopMenu />  {/* ÏÉÅÎã® Î©îÎâ¥ */}
      <AdminMenu />  {/* Í¥ÄÎ¶¨Ïûê Î©îÎâ¥ */}

      {/* Î°úÍ∑∏Ïù∏/Î°úÍ∑∏ÏïÑÏõÉ Î≤ÑÌäº */}
      {!isLoggedIn ? (
        <form onSubmit={handleLogin}>
          <input
            type="text"
            placeholder="ÏïÑÏù¥Îîî"
            value={loginForm.username}
            onChange={(e) => setLoginForm({ ...loginForm, username: e.target.value })}
            required
          />
          <input
            type="password"
            placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏"
            value={loginForm.password}
            onChange={(e) => setLoginForm({ ...loginForm, password: e.target.value })}
            required
          />
          <button type="submit">Î°úÍ∑∏Ïù∏</button>
        </form>
      ) : (
        <div className="auth-buttons">
          <button onClick={handleLogout}>Î°úÍ∑∏ÏïÑÏõÉ</button>
          {isAdmin && (
            <button 
              onClick={() => navigate('/staff/register')} 
              className="register-btn"
            >
              ÏßÅÏõê Îì±Î°ù
            </button>
          )}
        </div>
      )}

      {/* ÎùºÏö∞Ìä∏ ÏÑ§Ï†ï */}
      <Routes>
        <Route path="/" element={<ItemList />} />  {/* Î¨ºÍ±¥ Î™©Î°ù ÌéòÏù¥ÏßÄ */}
        <Route path="/staff/edit" element={<StaffEdit />} />  {/* ÏßÅÏõê ÏàòÏ†ï ÌéòÏù¥ÏßÄ */}
        <Route path="/stuff/item/list" element={<ItemList onAddToCart={handleAddToCart} isLoggedIn={isLoggedIn} />} />  {/* Î¨ºÍ±¥ Î™©Î°ù ÌéòÏù¥ÏßÄ */}
        <Route path="/stuff/item/register" element={<ItemRegister />} />  {/* Î¨ºÍ±¥ Îì±Î°ù ÌéòÏù¥ÏßÄ */}
        <Route path="/stuff/item/deleted" element={<DeletedItems />} />  {/* ÏÇ≠Ï†úÎêú Î¨ºÍ±¥ Î™©Î°ù ÌéòÏù¥ÏßÄ */}
        <Route path="/stuff/cart" element={<Cart />} />  {/* Ïû•Î∞îÍµ¨Îãà ÌéòÏù¥ÏßÄ */}
        <Route path="/staff/removelist" element={<RemovedStaff />} />  {/* ÏÇ≠Ï†úÎêú ÏßÅÏõê Î™©Î°ù */}
        <Route path="/staff/register" element={<Register />} />  {/* ÏßÅÏõê Îì±Î°ù ÌéòÏù¥ÏßÄ */}
      </Routes>

      {/* Í¥ÄÎ¶¨ÏûêÏùº Í≤ΩÏö∞ÏóêÎßå ÏßÅÏõê Î™©Î°ù ÌÖåÏù¥Î∏î ÌëúÏãú */}
      {isAdmin && <StaffTable />}
    </div>
  );
}

export default App;
