import axios from 'axios';

const BASE_URL = 'http://localhost:8080/mvc';

// axios 인스턴스 생성
const api = axios.create({
  baseURL: BASE_URL,
  withCredentials: true
});

// 물건(Stuff) 관련 API
export const stuffAPI = {
  // 물건 목록 조회
  getItemList: () => api.get('/stuff/item/list'),
  
  // 물건 등록
  registerItem: (itemData) => api.post('/stuff/item/register', itemData),
  
  // 물건 수정
  updateItem: (itemData) => api.post('/stuff/item/edit', itemData),
  
  // 물건 삭제
  deleteItem: (itemId) => api.post('/stuff/item/delete', { itemId }),
  
  // 장바구니 관련
  getCartItems: () => api.get('/stuff/cart'),
  addToCart: (itemId, quantity) => api.post('/stuff/cart/add', { itemId, quantity }),
  removeFromCart: (cartId) => api.post('/stuff/cart/remove', { cartId }),
  checkout: () => api.post('/stuff/cart/checkout')
};

// 직원(Staff) 관련 API
export const staffAPI = {
  // 로그인
  login: (staffId, password) => api.post('/staff/login', { staffId, password }),
  
  // 로그아웃
  logout: () => api.get('/staff/logout'),
  
  // 직원 목록 조회 (관리자용)
  getStaffList: () => api.get('/staff/list'),
  
  // 직원 삭제 (관리자용)
  deleteStaff: (bno) => api.get(`/staff/remove?bno=${bno}`),
  
  // 직원 수정
  updateStaff: (staffData) => api.post('/staff/edit', staffData),
  
  // 삭제된 직원 목록 조회
  getDeletedStaffList: () => api.get('/staff/removelist'),
  
  // 직원 복구
  restoreStaff: (bno) => api.post('/staff/restore', { bno }),
  
  // 비밀번호 변경
  changePassword: (bno, currentPassword, newPassword) => 
    api.post('/staff/changePassword', {
      bno,
      currentPassword,
      password: newPassword
    })
};

// 응답 인터셉터 추가
api.interceptors.response.use(
  response => response,
  error => {
    // 401 에러 처리 (인증 실패)
    if (error.response && error.response.status === 401) {
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

export default api;
