import axios from 'axios';
import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [shopItems, setShopItems] = useState([]);
  const [cartItems, setCartItems] = useState([]);
  const [isAdmin, setIsAdmin] = useState(false);
  const [deletedItems, setDeletedItems] = useState([]);

  useEffect(() => {
    shop();
    checkAdminStatus();
    loadCart();
  }, []);

  // 관리자 상태 확인
  function checkAdminStatus() {
    axios.get('http://localhost:8080/mvc/staff/check-admin')
      .then(response => {
        setIsAdmin(response.data.isAdmin);
      })
      .catch(error => {
        console.error('관리자 상태 확인 실패:', error);
      });
  }

  // 상품 목록 조회
  function shop() {
    axios.get('http://localhost:8080/mvc/stuff/item/list')
      .then(response => {
        const items = response.data.items || response.data;
        setShopItems(Array.isArray(items) ? items : []);
      })
      .catch(error => {
        console.error('상품 목록 조회 실패:', error);
        setShopItems([]);
      });
  }

  // 장바구니 조회
  function loadCart() {
    axios.get('http://localhost:8080/mvc/stuff/cart')
      .then(response => {
        setCartItems(response.data);
      })
      .catch(error => {
        console.error('장바구니 조회 실패:', error);
      });
  }

  // 장바구니에 추가
  function addItem(itemId, quantity) {
    axios.post('http://localhost:8080/mvc/stuff/cart/add', {
      itemId: itemId,
      quantity: quantity
    })
    .then(response => {
      alert('상품이 장바구니에 추가되었습니다.');
      loadCart();
    })
    .catch(error => {
      alert('장바구니 추가에 실패했습니다.');
    });
  }

  // 장바구니에서 제거
  function removeFromCart(cartId) {
    axios.post(`http://localhost:8080/mvc/stuff/cart/remove?cartId=${cartId}`)
      .then(() => {
        alert('상품이 장바구니에서 제거되었습니다.');
        loadCart();
      })
      .catch(error => {
        alert('상품 제거에 실패했습니다.');
      });
  }

  // 결제 처리
  function checkout() {
    axios.post('http://localhost:8080/mvc/stuff/cart/checkout')
      .then(() => {
        alert('결제가 완료되었습니다.');
        loadCart();
      })
      .catch(error => {
        alert('결제에 실패했습니다.');
      });
  }

  // 관리자 기능: 상품 삭제
  function deleteItem(itemId) {
    if (window.confirm('정말 이 상품을 삭제하시겠습니까?')) {
      axios.post(`http://localhost:8080/mvc/stuff/item/delete?itemId=${itemId}`)
        .then(() => {
          alert('상품이 삭제되었습니다.');
          shop();
        })
        .catch(error => {
          alert('상품 삭제에 실패했습니다.');
        });
    }
  }

  // 관리자 기능: 삭제된 상품 목록 조회
  function loadDeletedItems() {
    axios.get('http://localhost:8080/mvc/stuff/item/deleted')
      .then(response => {
        setDeletedItems(response.data);
      })
      .catch(error => {
        console.error('삭제된 상품 목록 조회 실패:', error);
      });
  }

  // 관리자 기능: 상품 복구
  function restoreItem(itemId) {
    axios.post(`http://localhost:8080/mvc/stuff/item/restore?itemId=${itemId}`)
      .then(() => {
        alert('상품이 복구되었습니다.');
        loadDeletedItems();
        shop();
      })
      .catch(error => {
        alert('상품 복구에 실패했습니다.');
      });
  }

  return (
    <div className="App">
      <h1>상품 목록</h1>

      {/* 상품 목록 */}
      <div className="shop-items">
        {Array.isArray(shopItems) && shopItems.map(item => (
          <div key={item.itemId} className="shop-item">
            <h3>{item.itemName}</h3>
            <p>가격: {item.price}원</p>
            <p>재고: {item.stock}개</p>
            {item.description && <p>설명: {item.description}</p>}
            <button onClick={() => addItem(item.itemId, 1)}>장바구니에 추가</button>
            {isAdmin && (
              <button onClick={() => deleteItem(item.itemId)}>상품 삭제</button>
            )}
          </div>
        ))}
      </div>

      {/* 장바구니 */}
      <div className="cart-section">
        <h2>장바구니</h2>
        {cartItems.map(item => (
          <div key={item.cartId} className="cart-item">
            <p>{item.itemName} - {item.quantity}개</p>
            <button onClick={() => removeFromCart(item.cartId)}>제거</button>
          </div>
        ))}
        {cartItems.length > 0 && (
          <button onClick={checkout}>결제하기</button>
        )}
      </div>

      {/* 관리자 전용: 삭제된 상품 목록 */}
      {isAdmin && (
        <div className="deleted-items-section">
          <h2>삭제된 상품 목록</h2>
          <button onClick={loadDeletedItems}>삭제된 상품 보기</button>
          {deletedItems.map(item => (
            <div key={item.itemId} className="deleted-item">
              <p>{item.itemName}</p>
              <button onClick={() => restoreItem(item.itemId)}>복구</button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default App;
