import axios from 'axios';
import React, { useState, useEffect } from 'react';
import './App.css';
import { useNavigate, useLocation } from 'react-router-dom';

// axios Í∏∞Î≥∏ ÏÑ§Ï†ï
axios.defaults.withCredentials = true;

// App Ïª¥Ìè¨ÎÑåÌä∏
function App() {
  // ÏÉÅÌÉú Î≥ÄÏàòÎì§
  const [isAdmin, setIsAdmin] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loginForm, setLoginForm] = useState({ username: '', password: '' });
  const [staffList, setStaffList] = useState([]);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    checkLoginStatus();
    loadStaffList();
  }, []);

  // Î°úÍ∑∏Ïù∏ ÏÉÅÌÉú ÌôïÏù∏
  function checkLoginStatus() {
    axios.get('http://localhost:8080/mvc/staff/check-login')
      .then(response => {
        setIsLoggedIn(response.data.isLoggedIn);
        setIsAdmin(response.data.isAdmin);
      })
      .catch(error => {
        console.error('Î°úÍ∑∏Ïù∏ ÏÉÅÌÉú ÌôïÏù∏ Ïã§Ìå®:', error);
        setIsLoggedIn(false);
        setIsAdmin(false);
      });
  }

  // Î°úÍ∑∏Ïù∏ Ï≤òÎ¶¨
  function handleLogin(e) {
    e.preventDefault();
    const params = new URLSearchParams();
    params.append('staffId', loginForm.username);
    params.append('password', loginForm.password);

    axios.post('http://localhost:8080/mvc/staff/login', params, {
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded'
      }
    })
      .then(response => {
        if (response.data.success) {
          setIsLoggedIn(true);
          setIsAdmin(response.data.isAdmin);
          navigate('/stuff/item/list'); // ÏàòÏ†ïÎêú Í≤ΩÎ°ú
        } else {
          alert(response.data.message);
        }
      })
      .catch(error => {
        alert('Î°úÍ∑∏Ïù∏Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      });
  }

  // Î°úÍ∑∏ÏïÑÏõÉ Ï≤òÎ¶¨
  function handleLogout() {
    axios.post('http://localhost:8080/mvc/staff/logout')
      .then(response => {
        if (response.data.success) {
          setIsLoggedIn(false);
          setIsAdmin(false);
          navigate('/'); // Î°úÍ∑∏ÏïÑÏõÉ ÌõÑ Î©îÏù∏ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
        }
      })
      .catch(error => {
        console.error('Î°úÍ∑∏ÏïÑÏõÉ Ïã§Ìå®:', error);
      });
  }

  // ÏßÅÏõê Î™©Î°ù Î∂àÎü¨Ïò§Í∏∞
  function loadStaffList() {
    axios.get('http://localhost:8080/mvc/staff/list')
      .then(response => {
        setStaffList(response.data || []);
      })
      .catch(error => {
        console.error('ÏßÅÏõê Î™©Î°ù Ï°∞Ìöå Ïã§Ìå®:', error);
      });
  }

  // ÏßÅÏõê ÏÇ≠Ï†ú
  function confirmDelete(bno) {
    if (window.confirm('Ïù¥ ÏßÅÏõêÏùÑ ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?')) {
      const params = new URLSearchParams();
      params.append('bno', bno);

      axios.post('http://localhost:8080/mvc/staff/remove', params, {
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded'
        }
      })
        .then(response => {
          if (response.data.success) {
            alert('ÏßÅÏõêÏù¥ ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.');
            loadStaffList();
          } else {
            alert(response.data.message);
          }
        })
        .catch(error => {
          alert('ÏßÅÏõê ÏÇ≠Ï†úÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
        });
    }
  }

  // ÏßÅÏõê ÏàòÏ†ï
  function editStaff(bno) {
    axios.post('http://localhost:8080/mvc/staff/edit?bno=${bno}');
  }

  // TopMenu Ïª¥Ìè¨ÎÑåÌä∏
  const TopMenu = () => (
    <div className="top-menu">
      <h1 className="section-title" style={{ margin: 0 }}>Î¨ºÍ±¥ Î™©Î°ù</h1>
      <div>
        {location.search.includes('message=addedToCart') && (
          <span style={{ color: 'green', marginRight: '20px' }}>
            Ïû•Î∞îÍµ¨ÎãàÏóê Ï∂îÍ∞ÄÎêòÏóàÏäµÎãàÎã§!
          </span>
        )}
        <a href="/mvc/stuff/cart" className="cart-link">
          üõí Ïû•Î∞îÍµ¨Îãà
        </a>
      </div>
    </div>
  );

  // AdminMenu Ïª¥Ìè¨ÎÑåÌä∏
  const AdminMenu = () => (
    isAdmin && (
      <div className="admin-menu">
        <button onClick={() => navigate('/mvc/stuff/item/register')}>Î¨ºÍ±¥ Îì±Î°ù</button>
        <button onClick={() => navigate('/mvc/stuff/item/deleted')}>ÏÇ≠Ï†úÎêú Î¨ºÍ±¥ Î™©Î°ù</button>
        <button onClick={() => navigate('/mvc/staff/removelist')}>ÏÇ≠Ï†úÎêú ÏßÅÏõê Î™©Î°ù</button>
      </div>
    )
  );

  // StaffTable Ïª¥Ìè¨ÎÑåÌä∏
  const StaffTable = () => (
    isAdmin && (
      <>
        <h2 className="section-title">ÏßÅÏõê Î™©Î°ù</h2>
        <table className="staff-table">
          <thead>
            <tr>
              <th>ÏßÅÏõêÎ≤àÌò∏</th>
              <th>ÏïÑÏù¥Îîî</th>
              <th>Ïù¥Î¶Ñ</th>
              <th>Í¥ÄÎ¶¨Ïûê Ïó¨Î∂Ä</th>
              <th>Í¥ÄÎ¶¨</th>
            </tr>
          </thead>
          <tbody>
            {staffList.map(staff => (
              <tr key={staff.bno}>
                <td>{staff.bno}</td>
                <td>{staff.id}</td>
                <td>{staff.btext}</td>
                <td>{staff.admins === 1 ? 'Í¥ÄÎ¶¨Ïûê' : 'ÏùºÎ∞ò ÏßÅÏõê'}</td>
                <td>
                  <button onClick={() => confirmDelete(staff.bno)}>ÏÇ≠Ï†ú</button>
                  <button onClick={() => editStaff(staff.bno)}>ÏàòÏ†ï</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </>
    )
  );

  return (
    <div className="App">
      <TopMenu />
      <AdminMenu />

      {/* Î°úÍ∑∏Ïù∏/Î°úÍ∑∏ÏïÑÏõÉ Î≤ÑÌäº */}
      {!isLoggedIn ? (
        <form onSubmit={handleLogin}>
          <input
            type="text"
            placeholder="ÏïÑÏù¥Îîî"
            value={loginForm.username}
            onChange={(e) => setLoginForm({ ...loginForm, username: e.target.value })}
            required
          />
          <input
            type="password"
            placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏"
            value={loginForm.password}
            onChange={(e) => setLoginForm({ ...loginForm, password: e.target.value })}
            required
          />
          <button type="submit">Î°úÍ∑∏Ïù∏</button>
        </form>
      ) : (
        <button onClick={handleLogout}>Î°úÍ∑∏ÏïÑÏõÉ</button>
      )}

      {/* ÏßÅÏõê Î™©Î°ù ÌÖåÏù¥Î∏î */}
      <StaffTable />
    </div>
  );
}

export default App;
