import axios from 'axios';
import React, { useState, useEffect } from 'react';
import './App.css';
import { Routes, Route, useNavigate, useLocation } from 'react-router-dom';
import StaffEdit from './components/StaffEdit';
import ItemList from './components/ItemList';
import ItemRegister from './components/ItemRegister';
import StaffRegister from './components/Register';
import DeletedItems from './components/DeletedItems';
import Cart from './components/Cart';
import RemovedStaff from './components/RemovedStaff';

// axios Í∏∞Î≥∏ ÏÑ§Ï†ï
axios.defaults.withCredentials = true;

// App Ïª¥Ìè¨ÎÑåÌä∏
function App() {
  // ÏÉÅÌÉú Î≥ÄÏàòÎì§
  const [isAdmin, setIsAdmin] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loginForm, setLoginForm] = useState({ username: '', password: '' });
  const [staffList, setStaffList] = useState([]);
  // const [adminList , setAdminList] = useState([]);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    checkLoginStatus();
    loadStaffList();
    // loadAdminList();
  }, []);

  // Î°úÍ∑∏Ïù∏ ÏÉÅÌÉú ÌôïÏù∏
  function checkLoginStatus() {
    axios.get('http://localhost:8080/mvc/staff/check-login')
      .then(response => {
        setIsLoggedIn(response.data.isLoggedIn);
        setIsAdmin(response.data.isAdmin);
      })
      .catch(error => {
        console.error('Î°úÍ∑∏Ïù∏ ÏÉÅÌÉú ÌôïÏù∏ Ïã§Ìå®:', error);
        setIsLoggedIn(false);
        setIsAdmin(false);
      });
  }

  // Î°úÍ∑∏Ïù∏ Ï≤òÎ¶¨
  function handleLogin(e) {
    e.preventDefault();
    const params = new URLSearchParams();
    params.append('staffId', loginForm.username);
    params.append('password', loginForm.password);

    axios.post('http://localhost:8080/mvc/staff/login', params, {
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded'
      }
    })
      .then(response => {
        if (response.data.success) {
          setIsLoggedIn(true);
          setIsAdmin(response.data.isAdmin);
          navigate('/stuff/item/list'); // ÏàòÏ†ïÎêú Í≤ΩÎ°ú
        } else {
          alert(response.data.message || 'Î°úÍ∑∏Ïù∏Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.'); // Î©îÏãúÏßÄ Í∞úÏÑ†
        }
      })
      .catch(error => {
        console.error('Î°úÍ∑∏Ïù∏ ÏöîÏ≤≠ Ïã§Ìå®:', error); // ÏΩòÏÜîÏóê ÏóêÎü¨ Î°úÍ∑∏ Ï∂îÍ∞Ä
        alert('Î°úÍ∑∏Ïù∏Ïóê Ïã§Ìå®ÌñàÏäµÎãàÎã§. ÏÑúÎ≤ÑÏóê Î¨∏Ï†úÍ∞Ä ÏûàÏäµÎãàÎã§.'); // ÏóêÎü¨ Î©îÏãúÏßÄ Í∞úÏÑ†
