  // Ïû•Î∞îÍµ¨ÎãàÏóê Î¨ºÍ±¥ Ï∂îÍ∞ÄÌïòÎäî Ìï®Ïàò
  const handleAddToCart = async (itemId, quantity = 1) => {
    try {
      if (!isLoggedIn) {
        alert('Î°úÍ∑∏Ïù∏Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.');
        return;
      }

      const params = new URLSearchParams();
      params.append('itemId', itemId);
      params.append('quantity', quantity);

      const response = await axios.post(
        'http://localhost:8080/stuff/api/cart/add',
        params,
        {
          withCredentials: true,
          headers: {
            'Content-Type': 'application/x-www-form-urlencoded'
          }
        }
      );

      if (response.data.status === 'success') {
        alert('Ïû•Î∞îÍµ¨ÎãàÏóê Ï∂îÍ∞ÄÎêòÏóàÏäµÎãàÎã§.');
        // ÏÑ†ÌÉùÏ†Å: Ïû•Î∞îÍµ¨ÎãàÎ°ú Ïù¥Îèô
        // navigate('/cart');
      } else {
        throw new Error(response.data.message);
      }
    } catch (error) {
      console.error('Ïû•Î∞îÍµ¨Îãà Ï∂îÍ∞Ä Ï§ë Ïò§Î•ò:', error);
      alert(error.response?.data?.message || 'Ïû•Î∞îÍµ¨Îãà Ï∂îÍ∞ÄÏóê Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
    }
  };

  return (
    <div className="App">
      <TopMenu />
