        <button onClick={handleLogout}>Î°úÍ∑∏ÏïÑÏõÉ</button>
      )}

      {/* ÎùºÏö∞Ìä∏ ÏÑ§Ï†ï ÏàòÏ†ï */}
      <Routes>
        {/* Î©îÏù∏ ÌéòÏù¥ÏßÄÏóê ItemList ÌëúÏãú */}
        <Route path="/" element={<ItemList onAddToCart={handleAddToCart} isLoggedIn={isLoggedIn} isAdmin={isAdmin} />} />
        <Route path="/staff/edit" element={<StaffEdit />} />
        <Route path="/stuff/item/list" element={<ItemList onAddToCart={handleAddToCart} isLoggedIn={isLoggedIn} isAdmin={isAdmin} />} />
        <Route path="/staff/register" element={<StaffRegister />} />
        <Route path="/stuff/item/register" element={<ItemRegister />} />
        <Route path="/stuff/item/deleted" element={<DeletedItems />} />
        <Route path="/stuff/cart" element={<Cart />} />
        <Route path="/staff/removelist" element={<RemovedStaff />} />
      </Routes>

      {/* Í¥ÄÎ¶¨ÏûêÏùº Í≤ΩÏö∞ÏóêÎßå StaffTable ÌëúÏãú */}
      {isAdmin && <StaffTable />}
    </div>
  );
}

export default App;
