import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import './ItemDetail.css';
import { SERVER_URL } from '../config';

function ItemDetail({ isLoggedIn, isAdmin }) {
  const { itemId } = useParams();
  const navigate = useNavigate();
  const [item, setItem] = useState(null);
  const [boards, setBoards] = useState([]);
  const [newBoard, setNewBoard] = useState({
    board_title: '',
    board_content: ''
  });
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const [currentComment, setCurrentComment] = useState(1);
  const [cpageSize, setCpageSize] = useState(5);
  const [totalComment, setTotalComment] = useState(0);
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [pageSize, setPageSize] = useState(10);
  const [totalPages, setTotalPages] = useState(0);

  // 상품 정보와 댓글 목록 로드
  useEffect(() => {
    const loadItemDetail = async () => {
      try {
        const response = await axios.get(`${SERVER_URL}/mvc/stuff/item/detail/${itemId}`, {
          withCredentials: true
        });
        
        if (response.data.status === 'success') {
          setItem(response.data.item);
        } else {
          throw new Error('상품 정보를 불러오는데 실패했습니다.');
        }
      } catch (error) {
        console.error('상품 정보 로딩 실패:', error);
        alert('상품 정보를 불러오는데 실패했습니다.');
        setItem(null);
      } finally {
        setLoading(false);
      }
    };

    // 댓글 목록 로드
    const loadComments = async (boardNo) => {
      try {
        const response = await axios.get('/board/commentlist', {
          params: { 
            board_no: boardNo, 
            currentComment: currentComment, 
            cpageSize: cpageSize 
          },
          withCredentials: true,
          headers: {
            'Content-Type': 'application/json',
            'Accept': 'application/json'
          }
        });

        if (response.data) {
          const { comments = [], totalComment = 0 } = response.data;
          setComments(comments);
          setTotalComment(totalComment);
        }
      } catch (error) {
        console.error('댓글 목록을 불러오는 중 오류 발생:', error);
        setComments([]);
        setTotalComment(0);
      }
    };

    loadItemDetail();
    loadComments(itemId);
  }, [itemId, currentComment]);

  // 댓글 작성 함수
  const handleCommentSubmit = async (e, boardNo) => {
    e.preventDefault();

    if (!newComment.trim()) {
      alert('댓글 내용을 입력해 주세요.');
      return;
    }

    try {
      // JSON 형식으로 데이터 전송
      const response = await axios.post(
        '/board/comment',
        {
          board_no: boardNo,
          comment_content: newComment,
          member_no: localStorage.getItem('member_no')
        },
        {
          withCredentials: true,
          headers: {
            'Content-Type': 'application/json'
          }
        }
      );

      if (response.data.success) {
        setNewComment('');
        loadComments(boardNo);
      } else {
        alert(response.data.message || '댓글 작성에 실패했습니다.');
      }
    } catch (error) {
      console.error('댓글 작성 중 오류 발생:', error);
      alert('댓글 작성에 실패했습니다.');
    }
  };

  const handleDelete = async (comment_no) => {
    try {
      if (!isAdmin) {
        alert('관리자 권한이 필요합니다.');
        return;
      }

      if (window.confirm('이 댓글을 삭제하시겠습니까?')) {
        const response = await axios.post(`${SERVER_URL}/mvc/comment/delete`, 
          { comment_no },
          {
            withCredentials: true,
            headers: {
              'Content-Type': 'application/json',
            },
          }
        );

        if (response.data.success) {
          alert('댓글이 삭제되었습니다.');
          const updatedComments = comments.filter(comment => comment.comment_no !== comment_no);
          setComments(updatedComments);
        }
      }
    } catch (error) {
      console.error('댓글 삭제 실패:', error);
      alert('댓글 삭제 중 오류가 발생했습니다.');
    }
  };

  const handleCommentPageChange = (page) => {
    if (page >= 1 && page <= totalComment) {
      setCurrentComment(page);
    }
  };

  if (loading) {
    return <div>로딩 중...</div>;
  }

  if (!item) {
    return <div>상품을 찾을 수 없습니다.</div>;
  }

  return (
    <div className="item-detail-container">
      <div className="item-detail">
        <div className="item-image">
          {item?.image_url ? (
            <img 
              src={item.image_url}
              alt={item.item_name}
              onError={(e) => {
                e.target.src = 'https://via.placeholder.com/400x200';
              }}
            />
          ) : (
            <img 
              src='https://via.placeholder.com/400x200'
              alt="기본 이미지"
            />
          )}
        </div>
        <div className="item-info">
          <h2>{item?.item_name}</h2>
          <p className="price">가격: {(item?.item_price || 0).toLocaleString()}원</p>
          <p className="stock">재고: {(item?.item_stock || 0).toLocaleString()}개</p>
          <p className="description">{item?.item_description}</p>
        </div>
      </div>

      <div className="comment-section">
        <h3>댓글</h3>
        
        {isLoggedIn && (
          <form onSubmit={(e) => handleCommentSubmit(e, itemId)} className="comment-form">
            <textarea
              value={newComment}
              onChange={(e) => setNewComment(e.target.value)}
              placeholder="댓글을 입력하세요"
              required
            />
            <button type="submit">댓글 작성</button>
          </form>
        )}

        <div className="comment-list">
          {!comments || comments.length === 0 ? (
            <p>등록된 댓글이 없습니다.</p>
          ) : (
            <div>
              {comments.map((comment) => (
                <div key={comment.comment_no} className="comment-item">
                  <p>{comment.comment_content}</p>
                  <div className="comment-info">
                    <span>{comment.comment_writedate}</span>
                    {isAdmin && (
                      <button
                        onClick={() => handleDelete(comment.comment_no)}
                        className="delete-button"
                      >
                        삭제
                      </button>
                    )}
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        <div className="pagination">
          <button onClick={() => handleCommentPageChange(currentComment - 1)} disabled={currentComment === 1}>
            이전
          </button>
          {Array.from({ length: totalComment }, (_, i) => (
            <button
              key={i + 1}
              onClick={() => handleCommentPageChange(i + 1)}
              className={currentComment === i + 1 ? 'active' : ''}
            >
              {i + 1}
            </button>
          ))}
          <button onClick={() => handleCommentPageChange(currentComment + 1)} disabled={currentComment === totalComment}>
            다음
          </button>
        </div>
      </div>
    </div>
  );
}

export default ItemDetail;
