import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import './ItemDetail.css';
import { SERVER_URL } from '../config';

function ItemDetail({ isLoggedIn, isAdmin }) {
  const { itemId } = useParams();
  const navigate = useNavigate();
  const [item, setItem] = useState(null);
  const [boards, setBoards] = useState([]);
  const [newBoard, setNewBoard] = useState({
    board_title: '',
    board_content: ''
  });
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const [currentComment, setCurrentComment] = useState(1);
  const [cpageSize, setCpageSize] = useState(5);
  const [totalComment, setTotalComment] = useState(0);
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [pageSize, setPageSize] = useState(10);
  const [totalPages, setTotalPages] = useState(0);
  const [userInfo, setUserInfo] = useState(null);
  const [rating, setRating] = useState(5);
  const [hover, setHover] = useState(0);

  // 상품 정보와 댓글 목록 로드
  useEffect(() => {
    const loadItemDetail = async () => {
      try {
        const response = await axios.get(`${SERVER_URL}/mvc/stuff/item/detail/${itemId}`, {
          withCredentials: true
        });
        
        if (response.data.status === 'success') {
          setItem(response.data.item);
        } else {
          throw new Error('상품 정보를 불러오는데 실패했습니다.');
        }
      } catch (error) {
        console.error('상품 정보 로딩 실패:', error);
        alert('상품 정보를 불러오는데 실패했습니다.');
        setItem(null);
      } finally {
        setLoading(false);
      }
    };

    // 댓글 목록 로드
    const loadComments = async (boardNo) => {
      try {
        const response = await axios.get('/board/commentlist', {
          params: { 
            board_no: boardNo, 
            currentComment: currentComment, 
            cpageSize: cpageSize 
          },
          withCredentials: true,
          headers: {
            'Content-Type': 'application/json',
            'Accept': 'application/json'
          }
        });

        if (response.data) {
          const { comments = [], totalComment = 0 } = response.data;
          setComments(comments);
          setTotalComment(totalComment);
        }
      } catch (error) {
        console.error('댓글 목록을 불러오는 중 오류 발생:', error);
        setComments([]);
        setTotalComment(0);
      }
    };

    // 로그인 상태 체크 및 사용자 정보 로드
    const storedUserInfo = localStorage.getItem('userInfo');
    if (storedUserInfo) {
      try {
        const parsedUserInfo = JSON.parse(storedUserInfo);
        setUserInfo(parsedUserInfo);
        console.log('저장된 사용자 정보:', parsedUserInfo);
      } catch (error) {
        console.error('사용자 정보 파싱 오류:', error);
      }
    }

    loadItemDetail();
    loadComments(itemId);
  }, [itemId, currentComment]);

  // 댓글 작성 함수
  const handleCommentSubmit = async (e, boardNo) => {
    e.preventDefault();

    if (!isLoggedIn) {
      alert('로그인이 필요한 서비스입니다.');
      navigate('/login');
      return;
    }

    const memberNo = localStorage.getItem('member_no');
    if (!memberNo) {
      alert('로그인 정보가 유효하지 않습니다. 다시 로그인해주세요.');
      navigate('/login');
      return;
    }

    if (!newComment.trim()) {
      alert('댓글 내용을 입력해 주세요.');
      return;
    }

    try {
      const boardResponse = await axios.get('/board/read', {
        params: { 
          board_no: itemId 
        },
        withCredentials: true
      });

      if (!boardResponse.data) {
        throw new Error('게시글 정보를 가져올 수 없습니다.');
      }

      const response = await axios.post(
        '/board/comment',
        {
          board_no: itemId,
          member_no: memberNo,
          comment_content: newComment,
          rating: rating
        },
        {
          withCredentials: true,
          headers: {
            'Content-Type': 'application/json'
          }
        }
      );

      if (response.data.success) {
        setNewComment('');
        // 댓글 목록 새로고침
        const loadComments = async () => {
          try {
            const response = await axios.get('/board/commentlist', {
              params: { 
                board_no: itemId,
                currentComment: currentComment, 
                cpageSize: cpageSize 
              },
              withCredentials: true
            });

            if (response.data) {
              setComments(response.data.comments || []);
              setTotalComment(response.data.totalComment || 0);
            }
          } catch (error) {
            console.error('댓글 목록을 불러오는 중 오류 발생:', error);
          }
        };
        
        await loadComments();
        alert(response.data.message || '댓글이 등록되었습니다.');
      } else {
        throw new Error(response.data.message || '댓글 등록에 실패했습니다.');
      }
    } catch (error) {
      console.error('댓글 작성 중 오류 발생:', error);
      if (error.response?.status === 401 || 
          error.response?.data?.message?.includes('세션')) {
        alert('로그인 정보가 유효하지 않습니다. 다시 로그인해주세요.');
        navigate('/login');
      } else {
        alert(error.response?.data?.message || '댓글 작성에 실패했습니다.');
      }
    }
  };

  const handleDelete = async (comment_no) => {
    try {
      if (!isAdmin) {
        alert('관리자 권한이 필요합니다.');
        return;
      }

      if (window.confirm('이 댓글을 삭제하시겠습니까?')) {
        const response = await axios.post(`${SERVER_URL}/mvc/comment/delete`, 
          { comment_no },
          {
            withCredentials: true,
            headers: {
              'Content-Type': 'application/json',
            },
          }
        );

        if (response.data.success) {
          alert('댓글이 삭제되었습니다.');
          const updatedComments = comments.filter(comment => comment.comment_no !== comment_no);
          setComments(updatedComments);
        }
      }
    } catch (error) {
      console.error('댓글 삭제 실패:', error);
      alert('댓글 삭제 중 오류가 발생했습니다.');
    }
  };

  const handleCommentPageChange = (page) => {
    if (page >= 1 && page <= totalComment) {
      setCurrentComment(page);
    }
  };

  if (loading) {
    return <div>로딩 중...</div>;
  }

  if (!item) {
    return <div>상품을 찾을 수 없습니다.</div>;
  }

  return (
    <div className="item-detail-container">
      <div className="item-detail">
        <div className="item-image">
          {item?.image_url ? (
            <img 
              src={item.image_url}
              alt={item.item_name}
              onError={(e) => {
                e.target.src = 'https://via.placeholder.com/400x200';
              }}
            />
          ) : (
            <img 
              src='https://via.placeholder.com/400x200'
              alt="기본 이미지"
            />
          )}
        </div>
        <div className="item-info">
          <h2>{item?.item_name}</h2>
          <p className="price">가격: {(item?.item_price || 0).toLocaleString()}원</p>
          <p className="stock">재고: {(item?.item_stock || 0).toLocaleString()}개</p>
          <p className="description">{item?.item_description}</p>
        </div>
      </div>

      <div className="comment-section">
        <h3>댓글</h3>
        
        {isLoggedIn ? (
          <form onSubmit={(e) => handleCommentSubmit(e, itemId)} className="comment-form">
            <div className="rating-input">
              <div className="star-rating">
                {[1, 2, 3, 4, 5].map((num) => (
                  <label key={num}>
                    <input
                      type="radio"
                      name="rating"
                      value={num}
                      checked={rating === num}
                      onChange={() => setRating(num)}
                    />
                    <span 
                      className={`star ${num <= (hover || rating) ? 'on' : 'off'}`}
                      onMouseEnter={() => setHover(num)}
                      onMouseLeave={() => setHover(0)}
                    >
                      ★
                    </span>
                  </label>
                ))}
              </div>
              <span className="rating-text">별점: {rating}</span>
            </div>
            
            <textarea
              value={newComment}
              onChange={(e) => setNewComment(e.target.value)}
              placeholder="댓글을 입력하세요"
              required
            />
            <button type="submit">댓글 작성</button>
          </form>
        ) : (
          <p>댓글을 작성하려면 <span onClick={() => navigate('/login')} style={{color: 'blue', cursor: 'pointer'}}>로그인</span>이 필요합니다.</p>
        )}

        <div className="comment-list">
          {!comments || comments.length === 0 ? (
            <p>등록된 댓글이 없습니다.</p>
          ) : (
            comments.map((comment, index) => (
              <div 
                key={comment.comment_no ? `comment-${comment.comment_no}` : `comment-temp-${index}`} 
                className="comment-item"
              >
                <div className="comment-header">
                  <div className="star-display">
                    {[1, 2, 3, 4, 5].map((num) => (
                      <span 
                        key={`star-${comment.comment_no || index}-${num}`}
                        className={`star ${num <= (comment.rating || 0) ? 'on' : 'off'}`}
                      >
                        ★
                      </span>
                    ))}
                  </div>
                  <span>{comment.comment_writedate}</span>
                </div>
                <p>{comment.comment_content}</p>
                {isAdmin && (
                  <button
                    onClick={() => handleDelete(comment.comment_no)}
                    className="delete-button"
                  >
                    삭제
                  </button>
                )}
              </div>
            ))
          )}
        </div>

        <div className="pagination">
          <button 
            onClick={() => handleCommentPageChange(currentComment - 1)} 
            disabled={currentComment === 1}
          >
            이전
          </button>
          {totalComment > 0 && [...Array(totalComment)].map((_, i) => (
            <button
              key={`page-${i + 1}`}
              onClick={() => handleCommentPageChange(i + 1)}
              className={currentComment === i + 1 ? 'active' : ''}
            >
              {i + 1}
            </button>
          ))}
          <button 
            onClick={() => handleCommentPageChange(currentComment + 1)} 
            disabled={currentComment === totalComment}
          >
            다음
          </button>
        </div>
      </div>
    </div>
  );
}

export default ItemDetail;
