import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Header.css';
import { API_BASE_URL, CLIENT_URL } from '../config';
import logoImage from '../image/TBMALL.png';  // 이미지 import

function Header({ 
  isLoggedIn, 
  isAdmin, 
  userInfo, 
  handleAttendanceCheck,
  setIsLoggedIn,
  setIsAdmin,
  setUserInfo
}) {
  const navigate = useNavigate();
  const [memberId, setMemberId] = useState('');
  const [memberPw, setMemberPw] = useState('');
  const [isLoggedInState, setIsLoggedInState] = useState(isLoggedIn);
  const [isAdminState, setIsAdminState] = useState(isAdmin);
  const [userInfoState, setUserInfoState] = useState(userInfo);

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const params = new URLSearchParams();
      params.append('staffId', memberId);
      params.append('password', memberPw);

      const response = await axios.post(`${API_BASE_URL}/staff/login`, params, {
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded'
        },
        withCredentials: true,
      });

      if (response.data.success) {
        setIsLoggedInState(true);
        setIsAdminState(response.data.isAdmin);
        
        const positionNo = response.data.position_no;
        console.log("포지션 번호 확인:", positionNo);

        setUserInfoState({
          name: response.data.name || '',
          points: response.data.points || 0,
          position_no: positionNo || 0,
        });

        localStorage.setItem('userInfo', JSON.stringify({
          name: response.data.name || '',
          points: response.data.points || 0,
          position_no: positionNo || 0,
        }));

        localStorage.setItem('member_no', response.data.member_no || '');
        console.log('stored member_no:', response.data.member_no);
        console.log(response.data.isAdmin);
        
        window.location.reload();
      } else {
        alert(response.data.message || '로그인에 실패했습니다.');
      }
    } catch (error) {
      console.error('로그인 에러:', error);
      alert('로그인에 실패했습니다. 서버에 문제가 있습니다.');
    }
  };

  const handleLogout = async () => {
    try {
      const response = await axios.post(`${API_BASE_URL}/staff/logout`, {}, {
        withCredentials: true
      });

      if (response.data.success) {
        window.location.reload();
      }
    } catch (error) {
      console.error('로그아웃 에러:', error);
    }
  };

  return (
    <header className="header">
      <div className="header-content">
        <div className="logo" onClick={() => navigate('/')}>
          <img src={logoImage} alt="떠발이 직장인 로고" style={{height: "50px"}} />
          <h1>떠발이 직장인</h1>
        </div>
        
        <nav className="main-nav">
          <ul>
            <li onClick={() => navigate('/board/list')}>게시판</li>
            {isLoggedInState && (
              <li onClick={() => navigate('/stuff/cart')}>장바구니</li>
            )}
          </ul>
        </nav>

        <div className="auth-section">
          {!isLoggedInState ? (
            <form className="login-form" onSubmit={handleLogin}>
              <input
                type="text"
                placeholder="아이디"
                value={memberId}
                onChange={(e) => setMemberId(e.target.value)}
              />
              <input
                type="password"
                placeholder="비밀번호"
                value={memberPw}
                onChange={(e) => setMemberPw(e.target.value)}
              />
              <button type="submit">로그인</button>
            </form>
          ) : (
            <div className="user-menu">
              {userInfoState && (
                <span>
                  {userInfoState.name}님 안녕하세요. 잔여 포인트: {userInfoState.points}
                </span>
              )}
              <button onClick={handleLogout}>로그아웃</button>
              <button
                onClick={handleAttendanceCheck}
                style={{
                  marginLeft: "10px",
                  backgroundColor: "#4CAF50",
                  color: "white", 
                  border: "none",
                  padding: "10px 15px",
                  cursor: "pointer",
                }}
              >
                출석 체크
              </button>
            </div>
          )}
        </div>

        {isAdminState && (
          <div className="admin-nav">
            <ul>
              <li onClick={() => navigate('/stuff/item/register')}>물건 등록</li>
              <li onClick={() => navigate('/staff/register')}>직원 등록</li>
              <li onClick={() => navigate('/stuff/item/deleted')}>삭제된 물건</li>
              <li onClick={() => navigate('/staff/removelist')}>직원 관리</li>
            </ul>
          </div>
        )}
      </div>
    </header>
  );
}

export default Header;
